UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "C\'est fascinant, cela correspond aux théories selon lesquelles Un\'goro était la demeure des Titans lorsqu\'ils habitaient Kalimdor. Peut-être qu\'Aman\'thul lui-même veillait sur les \"Terres des Dieux\".", Text1_lang = "C\'est fascinant, cela correspond aux théories selon lesquelles Un\'goro était la demeure des Titans lorsqu\'ils habitaient Kalimdor. Peut-être qu\'Aman\'thul lui-même veillait sur les \"Terres des Dieux\"." WHERE Id = 10851;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "", Text1_lang = "L\'armée des elfes de la nuit fut repoussée à travers Un\'Goro jusqu\'aux frontières du désert de Tanaris. Quelque chose à Un\'Goro empêcha les Qiraji de s\'emparer de la région. Je ne comprends pas tout à fait ce mot mais je crois qu\'il signifie \"Terres de Dieux\". Il est dit qu\'ils n\'ont pas pu \"prendre les Terres des Dieux\"." WHERE Id = 10850;
