UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "", Text1_lang = "Mon éducation se poursuit et votre savoir dépasse le mien. Si vous voulez en apprendre plus, parlez à Me\'lynn. Elle vous guidera." WHERE Id = 8084;
