UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Je peux vous assurer que votre garde-robe a vraiment besoin de soins.", Text1_lang = "" WHERE Id = 8059;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "", Text1_lang = "Vous perdez votre temps et vous gâchez le mien en venant ici solliciter des leçons, $N. Je n\'ai pas le temps de vous enseigner les bases. On est en guerre, ici, et nous sommes près du front. Ne revenez pas tant que vous n\'aurez pas les aptitudes requises." WHERE Id = 8058;
