UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Je ne suis pas là pour enseigner aux enfants à peine sortis des jupes de leur mère. Vous êtes loin d\'avoir le niveau que j\'exige de mes élèves. En fait, vous êtes la plus triste figure de $garçon:fille; que j\'aie jamais vue et j\'aimerais que vous décampiez.", Text1_lang = "" WHERE Id = 8054;
