UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "J\'ai d\'excellentes recettes à vous proposer, et mes prix sont imbattables !", Text1_lang = "" WHERE Id = 7739;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Glyx et moi avons un marché : il me fournit ce dont j\'ai besoin, je lui fournis de l\'or. Sérieusement ! Ce que cet escroc m\’a fait payer pour un malheureux sac de sang-royal... Il y a de quoi faire pleurer un gobelin adulte !", Text1_lang = "" WHERE Id = 7738;
