UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Vous ne parlez pas au bon orc. Je ne m\'occupe pas des novices. Allez voir le jeune Whuut, là-bas... Et faites attention à votre langage.", Text1_lang = "" WHERE Id = 7736;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Je cherche un peu d\'aide. Comme vous en avez la compétence, je suppose que vous le ferez, si toutefois vous en avez le cran.", Text1_lang = "" WHERE Id = 7735;
