UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Je n\'ai pas le temps, $N. Dites à Whuut de s\'en occuper. Ce chiot a mariné toute la journée. Il a passé trop de temps comme ça, il va finir par blesser quelqu\'un.", Text1_lang = "" WHERE Id = 7733;
