UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Arrêtez de me regarder comme ça. Je ne suis peut-être pas un aventurier comme vous, mais ça ne veut pas dire que je n\'ai pas de muscles. Mon travail demande de la force, croyez-moi.", Text1_lang = "" WHERE Id = 7713;
