UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Très bien. Coupez un autre lambeau de chair à l\'un de ces spécimens. Mettons à l\'épreuve ce que vous avez appris, et nous irons ensuite de l\'avant.", Text1_lang = "" WHERE Id = 7711;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "C\'est vrai, j\'étais humain jadis. Ce qui semble un point faible pourrait en fait travailler en notre faveur, $N, car je connais bien les faiblesses humaines.", Text1_lang = "" WHERE Id = 7710;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "", Text1_lang = "J\'apprécie votre compagnie, mais avouons-le : vous en savez plus que moi sur les potions. Parlez plutôt au docteur Marsh si vous voulez progresser dans votre art. C\'est un Réprouvé qui vit à l\'Apothicarium d\'Undercity." WHERE Id = 7709;
