UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "", Text1_lang = "Quel plaisir de voir un visage amical. L\'apothicaire Johaan ne me parle pas, à moins de vouloir quelque chose. Voilà trop longtemps qu\'il a quitté sa tombe. Un jour, je serai comme ça moi aussi. Ça fait peur. Mais peut-être qu\'alors ça ne m\'inquiètera plus..." WHERE Id = 7707;
