UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Bena travaille comme une possédée. Jour et nuit, je la vois penchée sur ces potions, sans qu\'elle ne s\'arrête pour dormir ou manger. Je sais bien que nos alliés morts-vivants ont besoin d\'aide, mais je crains qu\'elle ne finisse par y laisser sa santé.", Text1_lang = "" WHERE Id = 7704;
