UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Une autre leçon ? Très bien. D\’abord, relaxez-vous. Vous devez être calme pour bien remuer une potion.", Text1_lang = "" WHERE Id = 7699;
UPDATE broadcast_text_locale SET Locale = "frFR", Text_lang = "Vous avez tout l\'air de quelqu\'un qui cherche l\'aventure. Moi, j\'en avais déjà assez à l\'époque où les trolls sont arrivés. Aujourd\'hui, je prends les jours comme ils viennent. Et j\'entraîne les autres à courir l\'aventure à ma place.", Text1_lang = "" WHERE Id = 7698;
