UPDATE creature_ai_texts SET content_loc2 = "Contemplez le pouvoir de la Lumière ! Grâces et gloire à l\'Alliance !" WHERE entry = -10300;
UPDATE creature_ai_texts SET content_loc2 = "Les attaquants ne vaincront pas ! Force et honneur pour la Horde !" WHERE entry = -10301;
UPDATE creature_ai_texts SET content_loc2 = "Je n\'aurais jamais pensé que ce serait de l\'huile de mana ! Il va y avoir beaucoup de demande. Sauf pour Grek, bien sûr !" WHERE entry = -10302;
UPDATE creature_ai_texts SET content_loc2 = "Envoyez !" WHERE entry = -10303;
UPDATE creature_ai_texts SET content_loc2 = "Apprendre à lire, ça donne soif. À votre santé, d\’accord ?" WHERE entry = -10304;
UPDATE creature_ai_texts SET content_loc2 = "%s perd connaissance." WHERE entry = -10305;
UPDATE creature_ai_texts SET content_loc2 = "V\'nez là, $r. Vous voulez quand même pas risquer vot\' vie dans un donjon, sérieusement ? Faire un boulot dégueulasse, et pour quoi ? Une babiole sans valeur ? Pfff, c\'qu\'il vous faut vraiment, c\'est une de mes amulettes. Leur fonctionnement est garanti !" WHERE entry = -10306;
UPDATE creature_ai_texts SET content_loc2 = "Venez voir par ici, $c ! J\'ai un truc pour vous. Pile c\'qu\'il vous faut. C\'est du savon, voyez... et c\'est au bout d\'une corde ! Rien de personnel, $gmon pote:poupée;, mais ça commence à fouetter un peu, si vous voyez c\'que j\'veux dire." WHERE entry = -10307;
UPDATE creature_ai_texts SET content_loc2 = "Hé hé, $r ! J\'ai exactement ce qu\'il vous faut. Vous avez déjà vu un tikbalang ? Sales bêtes. Elles vous emportent et...et c\'est pour ça qu\'il vous faut une protection contre les tikbalangs ! Garantie pour les chasser très, très loin !" WHERE entry = -10308;
UPDATE creature_ai_texts SET content_loc2 = "L\'coin est dangereux, pas vrai ? Savez c\'qu\'il vous faut ? Une protection. J\'peux vous aider à défier la mort en personne ! Portez cette follepierre autour d\'votre cou, voyez, et vous pourrez revenir si vous vous faites expédier dans le monde des esprits ! Une petite balade jusqu\'à vot\' cadavre, et vous revoilà, $gfrais et dispos:fraîche et dispose; !" WHERE entry = -10309;
UPDATE creature_ai_texts SET content_loc2 = "Alléchantes amulettes ! Renversantes raretés ! Même les joailliers de la dernière mode ne savent rien des nouvelles méthodes ancestrales de maître Eskrokar ! Améliorez-vous grâce à ces talismans magiques à prix bradés !" WHERE entry = -10310;
UPDATE creature_ai_texts SET content_loc2 = "Z\'avez l\'air fatigué, $c. J\'dois avoir quequ\'chose pour vous. Ces amulettes, elles peuvent vous aider à sauter par-d\'sus les obstacles et à nager dans les rivières comme si vous y étiez $gné:née;. Elles énergétisent la nourriture que vous mangez, pour que vos blessures se referment à vue d\'œil !" WHERE entry = -10311;
UPDATE creature_ai_texts SET content_loc2 = "Z\'avez l\'air d\'vous languir. L\'être cher qui vous délaisse, c\'est ça ? Pas d\'soucis, pas d\'soucis. Vous m\'prenez ce médaillon, vous l\'portez quand vous l\'revoyez, et vous aurez plus une minute à vous, $gmon grand:ma poulette; !" WHERE entry = -10312;
UPDATE creature_ai_texts SET content_loc2 = "Votre sang va repeindre les murs de Kil\'sorrau !" WHERE entry = -10313;
UPDATE creature_ai_texts SET content_loc2 = "Si personne aide, Grok va taper !" WHERE entry = -10314;
UPDATE creature_ai_texts SET content_loc2 = "Grok besoin aide. Pourquoi personne aide ?" WHERE entry = -10315;
UPDATE creature_ai_texts SET content_loc2 = "Mog\'dorg le Ratatiné dit Grok aller dans ville, trouver aide pour Masse-sanglante. Grok est allé." WHERE entry = -10316;
UPDATE creature_ai_texts SET content_loc2 = "Clan Flèchelame combattre sans honneur. Avoir gronns pour aider. Vous allez dans les Tranchantes, vous parlez à Mog\'dorg le Ratatiné. Vous tuez les gronns." WHERE entry = -10317;
UPDATE creature_ai_texts SET content_loc2 = "Grok être colère si petites créatures pas aller dans Tranchantes parler à Mog\'dorg !" WHERE entry = -10318;
UPDATE creature_ai_texts SET content_loc2 = "Grok a faim. Fruit a l\'air bon." WHERE entry = -10319;
UPDATE creature_ai_texts SET content_loc2 = "%s entre dans une rage folle." WHERE entry = -10320;
UPDATE creature_ai_texts SET content_loc2 = "%s est épuisé." WHERE entry = -10321;
UPDATE creature_ai_texts SET content_loc2 = "Rien de tel qu\'une bonne petite chopine pour faire glisser un de mes merveilleux muffins." WHERE entry = -10322;
UPDATE creature_ai_texts SET content_loc2 = "Ça fait du bien par où ça passe. Muffins à vendre ! Achetez vos muffins frais !" WHERE entry = -10323;
UPDATE creature_ai_texts SET content_loc2 = "Et ouais, je suis Monsieur Muffin. Et pourquoi ?! Mais parce que je vends des muffins, pardi !" WHERE entry = -10324;
UPDATE creature_ai_texts SET content_loc2 = "J\'ai des muffins, ils sont beaux mes muffins ! Venez donc jeter un œil !" WHERE entry = -10325;
UPDATE creature_ai_texts SET content_loc2 = "Cette terre m\’appartenait longtemps avant que vous et les vôtres n\’y posiez vos misérables pieds !" WHERE entry = -10326;
UPDATE creature_ai_texts SET content_loc2 = "Tout ce qui s\’aventure ici m\’appartient. Vous compris !" WHERE entry = -10327;
UPDATE creature_ai_texts SET content_loc2 = "%s se retourne pour faire face à sa fin imminente." WHERE entry = -10328;
UPDATE creature_ai_texts SET content_loc2 = "Oh non !" WHERE entry = -10329;
UPDATE creature_ai_texts SET content_loc2 = "%s contemple la scène avec incrédulité." WHERE entry = -10330;
UPDATE creature_ai_texts SET content_loc2 = "Ils l\'ont déclenchée !" WHERE entry = -10331;
UPDATE creature_ai_texts SET content_loc2 = "Vous ne vous en tirerez pas vivants !" WHERE entry = -10332;
UPDATE creature_ai_texts SET content_loc2 = "J\'aurai votre vie et votre énergie !" WHERE entry = -10333;
UPDATE creature_ai_texts SET content_loc2 = "Pour Kael\'thas !" WHERE entry = -10334;
UPDATE creature_ai_texts SET content_loc2 = "Vous en savez trop !" WHERE entry = -10335;
UPDATE creature_ai_texts SET content_loc2 = "Il ne faut pas qu\'ils s\'échappent !" WHERE entry = -10336;
UPDATE creature_ai_texts SET content_loc2 = "Tuez-les avant qu\'ils ne s\'enfuient !" WHERE entry = -10337;
UPDATE creature_ai_texts SET content_loc2 = "Ceci appartient à la halte Aile-de-feu. Vous ne poserez pas vos mains terreuses dessus !" WHERE entry = -10338;
UPDATE creature_ai_texts SET content_loc2 = "Mon arme avoir soif !" WHERE entry = -10339;
UPDATE creature_ai_texts SET content_loc2 = "Les yeux du %s brillent d\'une lueur rouge et il est pris d\'un ricanement dément !" WHERE entry = -10340;
UPDATE creature_ai_texts SET content_loc2 = "J\’étais avec le bataillon. On faisait une reco de routine le long de la route des os. On a envoyé l\’éclaireur en avant, au cas où quelque chose de dangereux pointerait." WHERE entry = -10341;
UPDATE creature_ai_texts SET content_loc2 = "Et ça ne rate pas, l\’éclaireur revient nous dire qu\’il y a des orcs plus loin sur la route. Ils ne nous avaient pas vus, alors on s\’est dit qu\’on allait s\’approcher et faire connaissance." WHERE entry = -10342;
UPDATE creature_ai_texts SET content_loc2 = "Des gros enfoirés rouges ! Jamais vu d\'orcs aussi gros. Des muscles commac ! Et des défenses énormes !" WHERE entry = -10343;
UPDATE creature_ai_texts SET content_loc2 = "%s hoche la tête." WHERE entry = -10344;
UPDATE creature_ai_texts SET content_loc2 = "Des gangr\'orcs..." WHERE entry = -10345;
UPDATE creature_ai_texts SET content_loc2 = "Je ne sais pas, grunt. Je ne sais vraiment pas." WHERE entry = -10346;
UPDATE creature_ai_texts SET content_loc2 = "Mais ce n\’est pas le pire. Avant que je puisse nous faire reculer, certains d\’entre eux nous ont vus et sont passés à l\’attaque." WHERE entry = -10347;
UPDATE creature_ai_texts SET content_loc2 = "J\’ai perdu certains de mes meilleurs soldats ce jour-là..." WHERE entry = -10348;
UPDATE creature_ai_texts SET content_loc2 = "Vot\' rata est prêt. Venez l\'chercher..." WHERE entry = -10349;
UPDATE creature_ai_texts SET content_loc2 = "Et si j\'apprends que l\'un de vous s\'est de nouveau plaint à Nazgrel, ce s\'ra l\'un de vous que j\'servirai la prochaine fois, pauvres minables..." WHERE entry = -10350;
UPDATE creature_ai_texts SET content_loc2 = "Si mon estomac ne menaçait pas de se détacher de mon corps et de m\'abandonner pour les déchets qu\'on donne aux prisonniers, je me sentirais insulté par cette... cette nourriture." WHERE entry = -10351;
UPDATE creature_ai_texts SET content_loc2 = "Est-ce que je sens une pointe de cannelle ? Ou bien est-ce de la muscade ? Quoi qu\'il en soit, une chose est sûre : personne ne cuisine un âne aussi bien que vous, cuistot." WHERE entry = -10352;
UPDATE creature_ai_texts SET content_loc2 = "J\'ai vu des gnomes travailler plus dur que toi, péon ! Des gnominettes avec des couettes ! ALLEZ, ON SE BOUGE !" WHERE entry = -10353;
UPDATE creature_ai_texts SET content_loc2 = "Des orcs de quelle sorte, chef ?" WHERE entry = -10354;
UPDATE creature_ai_texts SET content_loc2 = "Vous voulez dire..." WHERE entry = -10355;
UPDATE creature_ai_texts SET content_loc2 = "Mais comment c\'est possible, chef ? Je croyais que la malédiction avait été levée..." WHERE entry = -10356;
UPDATE creature_ai_texts SET content_loc2 = "Le chef de guerre a mis la tête d\’Arazzius à prix ! Pour le moment, ce bon à rien, ce déchet démoniaque est notre ennemi numéro un." WHERE entry = -10357;
UPDATE creature_ai_texts SET content_loc2 = "Ouais, on a des problèmes. C\’est clair comme de l\’eau de roche. La question est, qu\’est-ce qu\’on va faire ?" WHERE entry = -10358;
UPDATE creature_ai_texts SET content_loc2 = "Je ne vais pas vous mentir, soldats. On va se faire botter les fesses, ici ! Bien, qui parmi vous est un vrai orc, et est prêt à faire quelque chose pour changer cela ?" WHERE entry = -10359;
UPDATE creature_ai_texts SET content_loc2 = "Que voulez-vous de moi, chaman ?" WHERE entry = -10360;
UPDATE creature_ai_texts SET content_loc2 = "N\'avez-vous donc pas d\'yeux pour voir ? N\'avez-vous pas vu les démons qui sont à notre porte ? Je n\'ai pas assez de soldats pour en détacher ailleurs !" WHERE entry = -10361;
UPDATE creature_ai_texts SET content_loc2 = "Priez vos dieux, chaman. Peut-être vous viendront-ils en aide. La Horde, elle, ne le peut pas..." WHERE entry = -10362;
UPDATE creature_ai_texts SET content_loc2 = "Ouah ! Regarde ses muscles !" WHERE entry = -10363;
UPDATE creature_ai_texts SET content_loc2 = "Va-t-il gagner ? Peut-il gagner ?" WHERE entry = -10364;
UPDATE creature_ai_texts SET content_loc2 = "Il est tellement discipliné !" WHERE entry = -10365;
UPDATE creature_ai_texts SET content_loc2 = "Je veux être exactement comme ce combattant !" WHERE entry = -10366;
UPDATE creature_ai_texts SET content_loc2 = "Comment fait-il ça ?" WHERE entry = -10367;
UPDATE creature_ai_texts SET content_loc2 = "Il va forcément gagner ! Regarde-le !" WHERE entry = -10368;
UPDATE creature_ai_texts SET content_loc2 = "Je ne veux plus jouer !" WHERE entry = -10369;
UPDATE creature_ai_texts SET content_loc2 = "À couvert !" WHERE entry = -10370;
UPDATE creature_ai_texts SET content_loc2 = "Ma maman me manque." WHERE entry = -10371;
UPDATE creature_ai_texts SET content_loc2 = "OGRE ! C\'est toi l\'ogre ! Ha-ha !" WHERE entry = -10372;
UPDATE creature_ai_texts SET content_loc2 = "On peut s\'arrêter de courir, maintenant ?" WHERE entry = -10373;
UPDATE creature_ai_texts SET content_loc2 = "Ne vous approchez pas trop du lac, il est hanté !" WHERE entry = -10374;
UPDATE creature_ai_texts SET content_loc2 = "Chat ! C\'est toi !" WHERE entry = -10375;
UPDATE creature_ai_texts SET content_loc2 = "Sortez de mon chemin !" WHERE entry = -10376;
UPDATE creature_ai_texts SET content_loc2 = "Vous osez m\'affronter ? C\'est Illidan lui-même qui m\'a entraîné !" WHERE entry = -10377;
UPDATE creature_ai_texts SET content_loc2 = "Si je prends un péon à fainéanter, je le charge dans une catapulte et je l\'expédie jusqu\'au Temple noir !" WHERE entry = -10378;
UPDATE creature_ai_texts SET content_loc2 = "Si l\'un de vous veut prendre une pause, péons, le cimetière est juste là." WHERE entry = -10379;
UPDATE creature_ai_texts SET content_loc2 = "La dernière fois que quelqu\'un s\'est mis en tête de gagner la récompense promise pour Néantroche, tout ce qu\'on en a retrouvé, c\'est une fine couche d\'aventurier téméraire compacté !" WHERE entry = -10380;
UPDATE creature_ai_texts SET content_loc2 = "Il faudrait peut-être que je rassemble quelques agents et que nous y allions après cette journée." WHERE entry = -10381;
UPDATE creature_ai_texts SET content_loc2 = "Je ne peux pas croire que Papa Roulette pense sérieusement qu\’il va réussir à duper quelqu\’un, et à le pousser à se rendre à la manaforge B\’naar !" WHERE entry = -10382;
UPDATE creature_ai_texts SET content_loc2 = "On ne peut pas laisser cet éthérien retarder la construction de la fusée-de-néant X-52 !" WHERE entry = -10383;
UPDATE creature_ai_texts SET content_loc2 = "Néantroche ? Ce truc peut vous aplatir comme une crêpe d\'un seul mouvement de son gigantesque pied ! Non merci !" WHERE entry = -10384;
UPDATE creature_ai_texts SET content_loc2 = "Bron ! Hé, Bron ! Frappe-moi !" WHERE entry = -10385;
UPDATE creature_ai_texts SET content_loc2 = "Nan, c\'t\'un truc de fillette ça. Tape-moi avec ton marteau !" WHERE entry = -10386;
UPDATE creature_ai_texts SET content_loc2 = "VAS-Y, \'SPÈCE DE MAUVIETTE !" WHERE entry = -10387;
UPDATE creature_ai_texts SET content_loc2 = "... ÇA DÉCHIRE !" WHERE entry = -10388;
UPDATE creature_ai_texts SET content_loc2 = "Je ne comprends pas ! Vous êtes aveugle ? Vous ne voyez donc pas les démons qui tambourinent à notre porte ? Qui vais-je envoyer parler à vos amis chamans ? Désignez-les !" WHERE entry = -10389;
UPDATE creature_ai_texts SET content_loc2 = "Quand ma noirceur te dévore !" WHERE entry = -10390;
UPDATE creature_ai_texts SET content_loc2 = "Dans le Vide, personne ne vous entendra crier !" WHERE entry = -10391;
UPDATE creature_ai_texts SET content_loc2 = "Je vais envoyer ton âme au vide éternel !" WHERE entry = -10392;
UPDATE creature_ai_texts SET content_loc2 = "Les créatures qui hantent cet endroit sont puissantes, $gvoyageur:voyageuse;. Il vous faudra une bonne réserve de munitions si vous voulez espérer survivre. C\'est votre jour de chance : je propose des flèches, balles et armes de jet de qualité en toutes quantités. À un prix très raisonnable, si on considère les circonstances." WHERE entry = -10393;
UPDATE creature_ai_texts SET content_loc2 = "Ah, encore $gun puissant aventurier:une puissante aventurière;. Le Néant peut se révéler dangereux : il vous faudra les sorts les plus puissants dans votre arsenal. Je propose les composants de la meilleure qualité qu\'il soit, communs ou exotiques. Achetez ici, vous ne le regretterez pas !" WHERE entry = -10394;
UPDATE creature_ai_texts SET content_loc2 = "%s chuchote tout bas, avec révérence." WHERE entry = -10395;
UPDATE creature_ai_texts SET content_loc2 = "%s consulte les informations données dans l\'avis de recherche." WHERE entry = -10396;
UPDATE creature_ai_texts SET content_loc2 = "%s commence à se métamorphoser..." WHERE entry = -10397;
UPDATE creature_ai_texts SET content_loc2 = "%s se fend en deux !" WHERE entry = -10398;
UPDATE creature_ai_texts SET content_loc2 = "Comment osez-vous perturber le dîner du maître !" WHERE entry = -10399;
UPDATE creature_ai_texts SET content_loc2 = "Quel gâchis d\'en donner à quelqu\'un comme vous !" WHERE entry = -10400;
UPDATE creature_ai_texts SET content_loc2 = "C\'était un porto Pupellyverbos vieux de deux cents ans !" WHERE entry = -10401;
UPDATE creature_ai_texts SET content_loc2 = "Comment osez-vous renverser le vin du maître ?" WHERE entry = -10402;
UPDATE creature_ai_texts SET content_loc2 = "Étrangers insolents !" WHERE entry = -10403;
UPDATE creature_ai_texts SET content_loc2 = "Crétins indiscrets ! Vous le payerez de vos vies !" WHERE entry = -10404;
UPDATE creature_ai_texts SET content_loc2 = "Vous n\'en sortirez pas vivants..." WHERE entry = -10405;
UPDATE creature_ai_texts SET content_loc2 = "%s s\'élance vers l\'avant, observant soigneusement son ennemi." WHERE entry = -10406;
UPDATE creature_ai_texts SET content_loc2 = "%s s\'amuse de votre maladresse." WHERE entry = -10407;
UPDATE creature_ai_texts SET content_loc2 = "Garde-à-vous !" WHERE entry = -10408;
UPDATE creature_ai_texts SET content_loc2 = "Allons, calmez-vous. Nous avons beaucoup à faire et peu de temps pour le faire, mes amis." WHERE entry = -10409;
UPDATE creature_ai_texts SET content_loc2 = "J\'ai déjà combattu aux côtés des draeneï à Skettis, et dans la vallée d\'Ombrelune. Mais maintenant, nous sommes en Azeroth. Et la situation a l\'air très mauvaise." WHERE entry = -10410;
UPDATE creature_ai_texts SET content_loc2 = "Mais d\'abord, écoutez-moi, les sin\'dorei. Ici, on n\'est pas là pour rigoler... Ça va vous changer de ce à quoi vous avez été habitués chez les Clairvoyants. Faites votre part du boulot, ou remontez sur le bateau !" WHERE entry = -10411;
UPDATE creature_ai_texts SET content_loc2 = "Maintenant que ce point-là est éclairci, bienvenue sur l\'île de Quel\'Danas. Le Puits de soleil se trouve sur le plateau au sud, et le prince Kael\'thas se terre sur la terrasse des Magistères, à l\'est." WHERE entry = -10412;
UPDATE creature_ai_texts SET content_loc2 = "Nous sommes ici comme troupes de soutien pour les « aventuriers », c\'est leur petit nom, que les galonnés ont recrutés. Nous allons les aider à tenir les points clés de l\'île et baisser un peu la pression pour qu\'ils puissent faire leur boulot." WHERE entry = -10413;
UPDATE creature_ai_texts SET content_loc2 = "Mesdames, si vous n\'avez pas de question, vous pouvez aller demander des instructions supplémentaires et vos affectations à vos chefs d\'unités. C\'est tout pour aujourd\'hui. Bonne chance !" WHERE entry = -10414;
UPDATE creature_ai_texts SET content_loc2 = "Bienvenue au Puits de soleil. Malgré la température agréable et la belle vue, vous n\'êtes pas là pour les vacances, mesdames et messieurs !" WHERE entry = -10415;
UPDATE creature_ai_texts SET content_loc2 = "La situation actuelle ressemble à peu près à ça : on tient ce qu\'on peut, pendant que des gens plus expérimentés et mieux équipés vont chercher Kael\'thas." WHERE entry = -10416;
UPDATE creature_ai_texts SET content_loc2 = "Je sais, nous aussi nous pensions qu\'il était mort, mais ce n\'est pas le cas. Il essaie d\'utiliser le Puits de soleil pour invoquer le maître de la Légion ardente, Kil\'jaeden, en ce monde." WHERE entry = -10417;
UPDATE creature_ai_texts SET content_loc2 = "Je peux vous assurer que, s\'il réussit, ce que ce monde subira sera bien pire que tout ce qu\'a jamais enduré l\'Outreterre. Alors faisons en sorte que ça n\'arrive pas." WHERE entry = -10418;
UPDATE creature_ai_texts SET content_loc2 = "Les guerriers seront déployés pour tenir les points clés pendant que vous, les tireurs d\'élite, aurez pour mission de tenir les troupes volantes ennemies à distance." WHERE entry = -10419;
UPDATE creature_ai_texts SET content_loc2 = "Pour vos affectations précises, j\'attends les ordres. En attendant, restez ici et soyez prêts à partir d\'un instant à l\'autre !" WHERE entry = -10420;
UPDATE creature_ai_texts SET content_loc2 = "Bonjour, jeunes recrues. Je suis le sergent instructeur Bahduum. Pour les quelques jours à venir, je serai chargé de vous orienter." WHERE entry = -10421;
UPDATE creature_ai_texts SET content_loc2 = "L\'île de Quel\'Danas a été envahie par les troupes du prince Kael\'thas Haut-Soleil. Nos renseignements le donnaient pour mort. Nos renseignements se trompaient." WHERE entry = -10422;
UPDATE creature_ai_texts SET content_loc2 = "Là-haut, sur le plateau, lui et ses amis les démons essaient de rallumer le Puits de soleil et d\'ouvrir une faille pour ramener leur grand sachem, Kil\'jaeden, en ce bas monde." WHERE entry = -10423;
UPDATE creature_ai_texts SET content_loc2 = "Notre boulot est de dégager autant de cette chair à canon que possible, tout en aidant les spécialistes que les grands manitoux nous envoient." WHERE entry = -10424;
UPDATE creature_ai_texts SET content_loc2 = "Inutile de le préciser, beaucoup d\'entre nous ne rentreront pas chez eux. Mais si vous appliquez votre entraînement et gardez la tête froide, vous serez un grand atout pour nous." WHERE entry = -10425;
UPDATE creature_ai_texts SET content_loc2 = "C\'est tout ce que j\'avais à dire pour l\'instant. Restez sur le branle-bas, mes enfants." WHERE entry = -10426;
UPDATE creature_ai_texts SET content_loc2 = "Ordure du Soleil brisé ! Feu à volonté !" WHERE entry = -10427;
UPDATE creature_ai_texts SET content_loc2 = "Restez bien sur les démons. Aujourd\'hui, on ne s\'occupe pas de tuer les créatures du Fléau." WHERE entry = -10428;
UPDATE creature_ai_texts SET content_loc2 = "Faucon-dragon en approche à l\'ouest ! Abattez-moi $gce:cette:c; $c !" WHERE entry = -10429;
UPDATE creature_ai_texts SET content_loc2 = "Ouah ! Regarde ses muscles !" WHERE entry = -10430;
UPDATE creature_ai_texts SET content_loc2 = "Va-t-il gagner ? Peut-il gagner ?" WHERE entry = -10431;
UPDATE creature_ai_texts SET content_loc2 = "Il est tellement discipliné !" WHERE entry = -10432;
UPDATE creature_ai_texts SET content_loc2 = "Je veux être exactement comme ce combattant !" WHERE entry = -10433;
UPDATE creature_ai_texts SET content_loc2 = "Comment fait-il ça ?" WHERE entry = -10434;
UPDATE creature_ai_texts SET content_loc2 = "Il va forcément gagner ! Regarde-le !" WHERE entry = -10435;
UPDATE creature_ai_texts SET content_loc2 = "Chubis Chubisimo ! Répare-moi tout ça... On n\'a pas toute la journée !" WHERE entry = -10436;
UPDATE creature_ai_texts SET content_loc2 = "Combien je te paie ? Tu appelles ça du travail ?" WHERE entry = -10437;
UPDATE creature_ai_texts SET content_loc2 = "À ton âge j\'aurais pu terminer de réparer tous ces déchiqueteurs avant le petit-déjeuner et il me serait resté du temps pour mes autres tâches de la journée. Et tout ça dans la neige du Berceau-de-l\’Hiver, rien que ça. Et là-bas, les pentes sont toutes montantes !" WHERE entry = -10438;
UPDATE creature_ai_texts SET content_loc2 = "Chubis, quand tu auras terminé ici, pense à refaire briller les chromes de la Roulettemobile. Je veux emmener Mama faire un tour ce soir." WHERE entry = -10439;
UPDATE creature_ai_texts SET content_loc2 = "Je savais bien que je n\'aurais pas dû virer Beebo !" WHERE entry = -10440;
UPDATE creature_ai_texts SET content_loc2 = "D\'accord, si tu réussis à en réparer ne serait-ce qu\'un, je t\'accorde dix minutes de pause. Ça te va ? C\'est une bonne proposition, non ?" WHERE entry = -10441;
UPDATE creature_ai_texts SET content_loc2 = "Nous ne connaîtrons pas l\'échec !" WHERE entry = -10442;
UPDATE creature_ai_texts SET content_loc2 = "Mort au Dernier Gardien !" WHERE entry = -10443;
UPDATE creature_ai_texts SET content_loc2 = "Nous ne sommes pas défaits !" WHERE entry = -10444;
UPDATE creature_ai_texts SET content_loc2 = "D\'autres me remplaceront." WHERE entry = -10445;
UPDATE creature_ai_texts SET content_loc2 = "Une victoire mineure..." WHERE entry = -10446;
UPDATE creature_ai_texts SET content_loc2 = "Vos efforts sont... vains." WHERE entry = -10447;
UPDATE creature_ai_texts SET content_loc2 = "Vous ne nous arrêterez pas !" WHERE entry = -10448;
UPDATE creature_ai_texts SET content_loc2 = "Le sorcier sera vaincu !" WHERE entry = -10449;
UPDATE creature_ai_texts SET content_loc2 = "Il faut protéger la faille !" WHERE entry = -10450;
UPDATE creature_ai_texts SET content_loc2 = "Vous allez manquer de temps !" WHERE entry = -10451;
UPDATE creature_ai_texts SET content_loc2 = "La victoire ou la mort !" WHERE entry = -10452;
UPDATE creature_ai_texts SET content_loc2 = "Tremblez, mortels ! Tremblez, et agenouillez-vous devant votre destructeur !" WHERE entry = -10453;
UPDATE creature_ai_texts SET content_loc2 = "%s pousse un cri perçant pour appeler à l\'aide un kaliri proche." WHERE entry = -10454;
UPDATE creature_ai_texts SET content_loc2 = "Vous ! Vous n\'avez rien à faire ici !" WHERE entry = -10455;
UPDATE creature_ai_texts SET content_loc2 = "C\'est la fin du monde !" WHERE entry = -10459;
UPDATE creature_ai_texts SET content_loc2 = "Nous sommes condamnés !" WHERE entry = -10460;
UPDATE creature_ai_texts SET content_loc2 = "Minus $r... toi étais... $groi tout pourri:reine toute pourrie;." WHERE entry = -10461;
UPDATE creature_ai_texts SET content_loc2 = "Arg... moi être... mort." WHERE entry = -10462;
UPDATE creature_ai_texts SET content_loc2 = "Moi partir pour... Ogri\'la." WHERE entry = -10463;
UPDATE creature_ai_texts SET content_loc2 = "Grand honneur... moi tué par $gle roi:la reine;." WHERE entry = -10464;
UPDATE creature_ai_texts SET content_loc2 = "$GRoi:Reine; $n, moi mourir maintenant." WHERE entry = -10465;
UPDATE creature_ai_texts SET content_loc2 = "$GRoi:Reine; penser que... exister pour de vrai... Ogri\'la ?" WHERE entry = -10466;
UPDATE creature_ai_texts SET content_loc2 = "Comment vaincre ce qui n\'a pas de vie ?" WHERE entry = -10467;
UPDATE creature_ai_texts SET content_loc2 = "Pathétique. Vous ne pouvez donc pas faire mieux ?" WHERE entry = -10468;
UPDATE creature_ai_texts SET content_loc2 = "Vous allez connaître la peur !" WHERE entry = -10469;
UPDATE creature_ai_texts SET content_loc2 = "Vous êtes faibles ! ALLEZ, VENEZ !" WHERE entry = -10470;
UPDATE creature_ai_texts SET content_loc2 = "Votre ardeur au combat n\'est rien à côté de la mienne !" WHERE entry = -10471;
UPDATE creature_ai_texts SET content_loc2 = "Goûte à mon énergie pure !" WHERE entry = -10472;
UPDATE creature_ai_texts SET content_loc2 = "Votre mort servira d\'exemple à tous ceux qui oseraient poser le pied sur le territoire de l\'Ethereum !" WHERE entry = -10473;
UPDATE creature_ai_texts SET content_loc2 = "Fuyez tant qu\'il en est encore temps !" WHERE entry = -10474;
UPDATE creature_ai_texts SET content_loc2 = "Pour Salhadaar ! Pour le roi-nexus !" WHERE entry = -10475;
UPDATE creature_ai_texts SET content_loc2 = "Stupide $r $gjoufflu:joufflue:r; ! Arme être à moi maintenant ! Donne !" WHERE entry = -10476;
UPDATE creature_ai_texts SET content_loc2 = "%s se frotte contre $n et pète comme seul un ogre peut le faire." WHERE entry = -10477;
UPDATE creature_ai_texts SET content_loc2 = "Moi aussi aimer péter !" WHERE entry = -10478;
UPDATE creature_ai_texts SET content_loc2 = "Le %s se recroqueville de peur en voyant $gson roi:sa reine; en colère." WHERE entry = -10479;
UPDATE creature_ai_texts SET content_loc2 = "Oh, moi penser que ça pas bien marcher entre nous." WHERE entry = -10480;
UPDATE creature_ai_texts SET content_loc2 = "Quoi moi faire pas bien, $gmon roi:ma reine; ?" WHERE entry = -10481;
UPDATE creature_ai_texts SET content_loc2 = "Vous ne vous en tirerez pas, cette fois !" WHERE entry = -10482;
UPDATE creature_ai_texts SET content_loc2 = "Les sables du temps vont être dispersés aux quatre vents !" WHERE entry = -10483;
UPDATE creature_ai_texts SET content_loc2 = "L\'histoire va être réécrite !" WHERE entry = -10484;
UPDATE creature_ai_texts SET content_loc2 = "Que le siège commence !" WHERE entry = -10485;
UPDATE creature_ai_texts SET content_loc2 = "Vous ne pourrez jamais nous vaincre tous !" WHERE entry = -10486;
UPDATE creature_ai_texts SET content_loc2 = "Vous n\'arriverez à rien !" WHERE entry = -10487;
UPDATE creature_ai_texts SET content_loc2 = "Non ! La faille..." WHERE entry = -10488;
UPDATE creature_ai_texts SET content_loc2 = "Pour Lune-d\’Argent ! Pour le Haut-Soleil !" WHERE entry = -10489;
UPDATE creature_ai_texts SET content_loc2 = "Votre mort sera un hommage digne du roi !" WHERE entry = -10490;
UPDATE creature_ai_texts SET content_loc2 = "La bourse ou la vie ? En fait, je me contenterais de ta vie..." WHERE entry = -10491;
UPDATE creature_ai_texts SET content_loc2 = "De désespoir, %s commence à déchaîner ses pouvoirs !" WHERE entry = -10492;
UPDATE creature_ai_texts SET content_loc2 = "Mog\'dorg est un imbécile de penser que notre sauveur pourrait être issu de l\'une des petites races." WHERE entry = -10493;
UPDATE creature_ai_texts SET content_loc2 = "N\'écoutez pas les mensonges de Mog\'dorg ! Ogri\'la n\'est qu\'un mythe ! Nous bâtirons notre propre paradis ici même, dans le sang de ceux qui voudraient nous diviser pour nous asservir !" WHERE entry = -10494;
UPDATE creature_ai_texts SET content_loc2 = "C\'est le moment. Abattez toute créature qui se mettrait sur la route des clans !" WHERE entry = -10495;
UPDATE creature_ai_texts SET content_loc2 = "N\'apprendrons-nous donc jamais à travailler ensemble ? Ou allons-nous juste nous tuer les uns les autres et finir oubliés par l\'Histoire." WHERE entry = -10496;
UPDATE creature_ai_texts SET content_loc2 = "Ne vous prosternez pas devant la marionnette de Mog\'dorg ! Il veut remplacer nos suzerains gronns par un souverain de basse extraction. Pourquoi devrions-nous remplacer un maître par un autre ?" WHERE entry = -10497;
UPDATE creature_ai_texts SET content_loc2 = "Si seulement les chefs des clans m\'écoutaient. L\'unité est la seule solution. Tous les autres chemins mènent à notre destruction." WHERE entry = -10498;
UPDATE creature_ai_texts SET content_loc2 = "Nous devrions unir les clans ogres, pas nous battre les uns contre les autres." WHERE entry = -10499;
UPDATE creature_ai_texts SET content_loc2 = "Les clans ogres ne se laisseront pas assujettir. Pas par les gronns, et pas par $gun:une:r; minuscule $r comme vous !" WHERE entry = -10500;
UPDATE creature_ai_texts SET content_loc2 = "Mon message d\'unité s\'adresse aux clans ogres, pas à vous, $r !" WHERE entry = -10501;
UPDATE creature_ai_texts SET content_loc2 = "Je vous déclare $gennemi:ennemie; des clans ogres. La punition en est la mort !" WHERE entry = -10502;
UPDATE creature_ai_texts SET content_loc2 = "Je ne vous permettrai pas de tuer mes semblables ! Vous allez mourir !" WHERE entry = -10503;
UPDATE creature_ai_texts SET content_loc2 = "À moi ! Vous ne prendrez pas cet endroit !" WHERE entry = -10510;
UPDATE creature_ai_texts SET content_loc2 = "Dégagez, cet endroit est à nous !" WHERE entry = -10511;
UPDATE creature_ai_texts SET content_loc2 = "La puissance de la faille est à nous !" WHERE entry = -10512;
UPDATE creature_ai_texts SET content_loc2 = "Hal..." WHERE entry = -10513;
UPDATE creature_ai_texts SET content_loc2 = "Ouais, Nat ?" WHERE entry = -10514;
UPDATE creature_ai_texts SET content_loc2 = "J\'ai encore fait le même rêve la nuit dernière..." WHERE entry = -10515;
UPDATE creature_ai_texts SET content_loc2 = "Lequel ?" WHERE entry = -10516;
UPDATE creature_ai_texts SET content_loc2 = "Le rêve... Le rêve délirant. Tu te souviens ?" WHERE entry = -10517;
UPDATE creature_ai_texts SET content_loc2 = "Moulin-de-Tarren est détruit par une attaque incroyable des morts-vivants et devient à jamais l\'ennemie d\'Austrivage." WHERE entry = -10518;
UPDATE creature_ai_texts SET content_loc2 = "Tu imagines qu\'un truc pareil se passe, Hal ? Tu imagines ça ?" WHERE entry = -10519;
UPDATE creature_ai_texts SET content_loc2 = "Impossible !" WHERE entry = -10520;
UPDATE creature_ai_texts SET content_loc2 = "Et celui où des milliers de gens nous regardent depuis le ciel..." WHERE entry = -10521;
UPDATE creature_ai_texts SET content_loc2 = "Nous sommes comme des marionnettes pour eux : nous dansons, pleurons, combattons et disons des inepties pour leur amusement." WHERE entry = -10522;
UPDATE creature_ai_texts SET content_loc2 = "Des fois, même quand je suis réveillé, je les sens qui nous regardent. Peut-être même qu\'ils nous observent en ce moment même..." WHERE entry = -10523;
UPDATE creature_ai_texts SET content_loc2 = "Nat, j\'en ai entendu des salades dans ma vie, mais là c\'est vraiment le pompon." WHERE entry = -10524;
UPDATE creature_ai_texts SET content_loc2 = "J\'suis sur une montagne avec un vieil arbre énorme. Y a des elfes qui courent partout..." WHERE entry = -10525;
UPDATE creature_ai_texts SET content_loc2 = "Et d\'un seul coup, y a un démon de 100 mètres de haut qui arrive et qui se met à grimper à l\'arbre..." WHERE entry = -10526;
UPDATE creature_ai_texts SET content_loc2 = "Et comme si ça suffisait pas, le gros démon se fait tuer par des petits machins flottant qui brillent... Ah oui, et en plus dans celui-là j\'étais UNE elfe... Eh ouais..." WHERE entry = -10527;
UPDATE creature_ai_texts SET content_loc2 = "Dans ce rêve, j\'étais le meilleur pêcheur du monde. Je suis allé dans un endroit appelé Kalimdor..." WHERE entry = -10528;
UPDATE creature_ai_texts SET content_loc2 = "Et les gens venaient de partout dans le monde pour me rendre hommage... et apprendre à pêcher." WHERE entry = -10529;
UPDATE creature_ai_texts SET content_loc2 = "J\'suis une espèce de dieu de la pêche..." WHERE entry = -10530;
UPDATE creature_ai_texts SET content_loc2 = "Celui-là est vraiment sinistre... Le gamin du roi, Arthas... Il part combattre le mal, avec Uther... *Nat s\'essuie le front*" WHERE entry = -10531;
UPDATE creature_ai_texts SET content_loc2 = "Mais c\'est horrible, Arthas est consumé par le mal et devient maléfique lui aussi..." WHERE entry = -10532;
UPDATE creature_ai_texts SET content_loc2 = "Alors il revient voir le roi en faisant semblant d\'être gentil, il dégaine son épée et embroche le roi jusqu\'au coude... enfin tu vois... il le tue d\'un seul coup." WHERE entry = -10533;
UPDATE creature_ai_texts SET content_loc2 = "Et celui-là est complètement incroyable... Tu connais les deux butors jacasseurs, Aguilor et Dagharn ?" WHERE entry = -10534;
UPDATE creature_ai_texts SET content_loc2 = "Eh ben dans mon rêve, ils se débrouillent pour inventer un truc qui s\'appelle de la glace et le parfument à la fraise... Ben, pour résumer, ils finissent par faire fortune !" WHERE entry = -10535;
UPDATE creature_ai_texts SET content_loc2 = "Et comme si c\'était pas assez dingue, ils décident de laisser tomber la vente de glaces et de partir à l\'aventure... Ils voyagent partout et finissent par disparaître dans un portail quelque part. Ce rêve-là m\'a vraiment donné des sueurs froides..." WHERE entry = -10536;
UPDATE creature_ai_texts SET content_loc2 = "Celui où je suis au Fort-de-Durn et où l\'orc que Landenoire retient comme esclave personnel s\'échappe..." WHERE entry = -10537;
UPDATE creature_ai_texts SET content_loc2 = "Il finit par détruire tout le fort, libérer tous les orcs emprisonnés dans les camps et prendre le pouvoir comme roi des orcs... Chef de guerre, ou un truc comme ça." WHERE entry = -10538;
UPDATE creature_ai_texts SET content_loc2 = "C\'est dingue, hein ?" WHERE entry = -10539;
UPDATE creature_ai_texts SET content_loc2 = "Y a pas moyen !" WHERE entry = -10540;
UPDATE creature_ai_texts SET content_loc2 = "Ça n\'arrivera jamais, Nat." WHERE entry = -10541;
UPDATE creature_ai_texts SET content_loc2 = "Tu mets quoi dans tes pipes, Nat ?
." WHERE entry = -10542;
UPDATE creature_ai_texts SET content_loc2 = "Baissez d\'un ton, Helcular. Il y a des étrangers partout ici..." WHERE entry = -10543;
UPDATE creature_ai_texts SET content_loc2 = "Nécromancie. Cela s\'appelle de la nécromancie. Et oui, j\'ai le pouvoir de vous en conférer le don." WHERE entry = -10544;
UPDATE creature_ai_texts SET content_loc2 = "Cela ne vous regarde pas, Helcular. Vous n\'êtes ni membre du Kirin Tor, ni nécromancien." WHERE entry = -10545;
UPDATE creature_ai_texts SET content_loc2 = "Mais pour être complètement honnête, je n\'ai rien à faire de ce que pense le Kirin Tor ! Ce ne sont que des idiots, enfermés dans leur pensée archaïque." WHERE entry = -10546;
UPDATE creature_ai_texts SET content_loc2 = "Cela viendra, Helcular... Cela viendra en son temps..." WHERE entry = -10547;
UPDATE creature_ai_texts SET content_loc2 = "Vous pouvez donc m\'enseigner ce... ce..." WHERE entry = -10548;
UPDATE creature_ai_texts SET content_loc2 = "Et le Kirin Tor ? Que dit-il de la nécromancie ?" WHERE entry = -10549;
UPDATE creature_ai_texts SET content_loc2 = "Alors enseignez-moi, Kel\'Thuzad. Apprenez-moi tout ce que vous savez..." WHERE entry = -10550;
UPDATE creature_ai_texts SET content_loc2 = "Bien joué, soldats ! On allait pas laisser ces animaux de la Horde nous en remontrer !" WHERE entry = -10551;
UPDATE creature_ai_texts SET content_loc2 = "On va fondre tout ce bazar et utiliser le métal pour en faire des armes ! On va voir ce que la Légion pense de notre recette de saccageur gangrené servi à la sauce Alliance !" WHERE entry = -10552;
UPDATE creature_ai_texts SET content_loc2 = "Démontez tout, soldats ! On renvoie tout ça à Hurlevent !" WHERE entry = -10553;
UPDATE creature_ai_texts SET content_loc2 = "Ils peuvent nous en balancer une autre, de leurs boîtes de conserve aux hormones ! On va leur montrer de quel bois se chauffe l\'Alliance !" WHERE entry = -10554;
UPDATE creature_ai_texts SET content_loc2 = "De l\'énergie... à... consommer." WHERE entry = -10555;
UPDATE creature_ai_texts SET content_loc2 = "L\'ombre... vous... engloutira." WHERE entry = -10556;
UPDATE creature_ai_texts SET content_loc2 = "Les ténèbres... consument... l\'univers." WHERE entry = -10557;
UPDATE creature_ai_texts SET content_loc2 = "Cette enveloppe est vide." WHERE entry = -10558;
UPDATE creature_ai_texts SET content_loc2 = "Plus... de... vie." WHERE entry = -10559;
UPDATE creature_ai_texts SET content_loc2 = "Le Vide... m\'appelle." WHERE entry = -10560;
UPDATE creature_ai_texts SET content_loc2 = "Ah, bon sang ! C\'est un joli piège. Dommage que vous ne surviviez pas pour le raconter..." WHERE entry = -10561;
UPDATE creature_ai_texts SET content_loc2 = "Imbécile de druide ! Avoir divisé votre essence vous a affaibli, ce qui a ouvert la porte à mon maître. Il dominera le Rêve d\’émeraude. Votre esprit et votre corps périront ici !" WHERE entry = -10562;
UPDATE creature_ai_texts SET content_loc2 = "Vous pensez que les démons respectent les règles ? Ces bêtes sont plus sauvages que les orcs et plus rusées que les morts-vivants. N\’essayez pas d\’affronter la Légion selon les principes classiques de la guerre." WHERE entry = -10563;
UPDATE creature_ai_texts SET content_loc2 = "Combien de soldats devront mourir avant que nous commencions à faire un peu attention à ce qui se passe ici ?" WHERE entry = -10564;
UPDATE creature_ai_texts SET content_loc2 = "Cette guerre - et ne faites pas l\’erreur de croire que ce n\’est pas une guerre - ne va pas se gagner toute seule. Si nous ne faisons pas de notre mieux pour combattre, griffer, mordre et ruer pour chaque parcelle de cette terre brisée, tout est perdu." WHERE entry = -10565;
UPDATE creature_ai_texts SET content_loc2 = "J\’ai le regret de vous informer que nous avons renvoyé dix soldats dans leur foyer, aujourd\’hui... dans des sacs mortuaires." WHERE entry = -10566;
UPDATE creature_ai_texts SET content_loc2 = "%s piaffe d\'impatience." WHERE entry = -10567;
UPDATE creature_ai_texts SET content_loc2 = "%s envoie un regard mauvais aux gardes." WHERE entry = -10568;
UPDATE creature_ai_texts SET content_loc2 = "%s pousse un soupir de frustration." WHERE entry = -10569;
UPDATE creature_ai_texts SET content_loc2 = "%s regarde dans la tour et serre les mâchoires." WHERE entry = -10570;
UPDATE creature_ai_texts SET content_loc2 = "Allez, on accélère la cadence ! Les balistes ne vont pas se réparer toutes seules !" WHERE entry = -10571;
UPDATE creature_ai_texts SET content_loc2 = "Gardez le rythme, les gars ! Il faut déplacer le matériel et réparer les armes !" WHERE entry = -10572;
UPDATE creature_ai_texts SET content_loc2 = "Allez ! Nos gars sont là-bas, ils ont besoin de ce matériel !" WHERE entry = -10573;
UPDATE creature_ai_texts SET content_loc2 = "Je vais vous apprendre à ne plus vous trouver sur notre chemin !" WHERE entry = -10574;
UPDATE creature_ai_texts SET content_loc2 = "Je vais vous faire payer ça !" WHERE entry = -10575;
UPDATE creature_ai_texts SET content_loc2 = "Le seigneur de guerre Kalithresh entendra parler de cette insolence !" WHERE entry = -10576;
UPDATE creature_ai_texts SET content_loc2 = "Pathétique ver de terre !" WHERE entry = -10577;
UPDATE creature_ai_texts SET content_loc2 = "Imbéciles incompétents ! Auchindoun aurait dû être à nous ! Regardez-vous, à cavaler comme des rats !" WHERE entry = -10578;
UPDATE creature_ai_texts SET content_loc2 = "Allez, on se grouille ! Plus vous mettrez longtemps, plus je vous ferai mal !" WHERE entry = -10579;
UPDATE creature_ai_texts SET content_loc2 = "Debout ! On se lève et on retourne au travail !" WHERE entry = -10580;
UPDATE creature_ai_texts SET content_loc2 = "C\'est trop tôt ! Vous commencez à traînasser trop tôt !" WHERE entry = -10581;
UPDATE creature_ai_texts SET content_loc2 = "C\'est terrible... À force de vous cogner dessus, bande de péons flemmards, j\'en ai mal aux bras !" WHERE entry = -10582;
UPDATE creature_ai_texts SET content_loc2 = "Vous, retournez au travail !" WHERE entry = -10583;
UPDATE creature_ai_texts SET content_loc2 = "Hé ben alors ? Papa et môman ne t\’ont rien appris ?" WHERE entry = -10584;
UPDATE creature_ai_texts SET content_loc2 = "%s commence à incanter des soins massifs !" WHERE entry = -10585;
UPDATE creature_ai_texts SET content_loc2 = "Qui a osé abattre un des commandants de Kargath ? Vous allez le payer... de votre vie !" WHERE entry = -10586;
UPDATE creature_ai_texts SET content_loc2 = "Je vais dévorer vos chairs, comme les flammes de la Légion ardente dévoreront vos peuples !" WHERE entry = -10587;
UPDATE creature_ai_texts SET content_loc2 = "$gPressé:Pressée; de mourir, c\'est ça ?" WHERE entry = -10588;
UPDATE creature_ai_texts SET content_loc2 = "Votre mort est imminente, $n. Savourez bien vos derniers instants." WHERE entry = -10589;
UPDATE creature_ai_texts SET content_loc2 = "Je suis plus vieux que vous ne pouvez le concevoir. Ne me manquez pas de respect !" WHERE entry = -10590;
UPDATE creature_ai_texts SET content_loc2 = "Puissent vos dieux avoir pitié de vous. Car moi je n\'en aurai aucune." WHERE entry = -10591;
UPDATE creature_ai_texts SET content_loc2 = "Que les vents... me portent." WHERE entry = -10592;
UPDATE creature_ai_texts SET content_loc2 = "Servir le maître... avec plaisir." WHERE entry = -10593;
UPDATE creature_ai_texts SET content_loc2 = "Au final... vous échouerez." WHERE entry = -10594;
UPDATE creature_ai_texts SET content_loc2 = "Ma vie... pour le maître." WHERE entry = -10595;
UPDATE creature_ai_texts SET content_loc2 = "ATTAQUEZ DE TOUTE LA FORCE DE L\'AILE-DU-NÉANT, MES ENFANTS ! À L\'ATTAQUE !" WHERE entry = -10596;
UPDATE creature_ai_texts SET content_loc2 = "Seigneur Illidan, donne-moi le pouvoir du Vol !" WHERE entry = -10597;
UPDATE creature_ai_texts SET content_loc2 = "ÇA PAS COULEUR DE BIÈRE ! ÇA PAS GOÛT DE BIÈRE ! MAIS ÇA BIÈRE QUAND MÊME !! BIÈRE OGRE !!" WHERE entry = -10598;
UPDATE creature_ai_texts SET content_loc2 = "HÉ VOUS !! BOIVEZ LA BIÈRE OGRE !! POUR ÊTRE GRAND ET FORT !!" WHERE entry = -10599;
UPDATE creature_ai_texts SET content_loc2 = "VOUS VOULEZ DU BOIRE ?!! ON A DU BOIRE !!!" WHERE entry = -10600;
UPDATE creature_ai_texts SET content_loc2 = "Ils auraient dû m\'écouter. J\'aurais alors reçu tous les honneurs que je mérite !" WHERE entry = -10601;
UPDATE creature_ai_texts SET content_loc2 = "Je vous préviens que dès notre retour à l\’enclave des Pérégrins, vous aurez à rendre compte de tous vos actes, forestier Valanna ! Vous seule êtes responsable de nos pertes, et je suis certain que le capitaine sera de mon avis." WHERE entry = -10602;
UPDATE creature_ai_texts SET content_loc2 = "Est-ce que je ne leur avais pas crié : Reculez ! Reculez et regroupez-vous pour un tir de barrage !" WHERE entry = -10603;
UPDATE creature_ai_texts SET content_loc2 = "Prenez ça !" WHERE entry = -10604;
UPDATE creature_ai_texts SET content_loc2 = "Ce n\’est pas Lune-d\’Argent ! Où sommes-nous ? Oh, je crois que j\’ai eu une petite absence passagère." WHERE entry = -10605;
UPDATE creature_ai_texts SET content_loc2 = "Je vais certainement recevoir une décoration lorsque nous serons rentrés. Nous avons peut-être subi quelques pertes, mais dans l\’ensemble je trouve que nous nous en sommes très bien tirés. N\’est-ce pas, Valanna ?" WHERE entry = -10606;
UPDATE creature_ai_texts SET content_loc2 = "Vous devez être l\'officier le plus incompétent que j\'aie jamais vu !" WHERE entry = -10607;
UPDATE creature_ai_texts SET content_loc2 = "Imbécile." WHERE entry = -10608;
UPDATE creature_ai_texts SET content_loc2 = "Ouais, ouais. Cause toujours." WHERE entry = -10609;
UPDATE creature_ai_texts SET content_loc2 = "Est-ce que vous pouvez me rappeler pourquoi je ne suis pas déjà partie ?" WHERE entry = -10610;
UPDATE creature_ai_texts SET content_loc2 = "Est-ce que vous pourriez la fermer ?" WHERE entry = -10611;
UPDATE creature_ai_texts SET content_loc2 = "Avec tout le respect que je vous dois, chef, allez au diable !" WHERE entry = -10612;
UPDATE creature_ai_texts SET content_loc2 = "Pas lancer sort !" WHERE entry = -10613;
UPDATE creature_ai_texts SET content_loc2 = "Moi furieux. Écraser ta face !" WHERE entry = -10614;
UPDATE creature_ai_texts SET content_loc2 = "J\'ai colère ! Raaaahh !" WHERE entry = -10615;
UPDATE creature_ai_texts SET content_loc2 = "Petite chose idiote. Moi écraser !" WHERE entry = -10616;
UPDATE creature_ai_texts SET content_loc2 = "Meurs, sang-tiède !" WHERE entry = -10617;
UPDATE creature_ai_texts SET content_loc2 = "Pour le Maître !" WHERE entry = -10618;
UPDATE creature_ai_texts SET content_loc2 = "Illidan règne !" WHERE entry = -10619;
UPDATE creature_ai_texts SET content_loc2 = "Mon sang est comme du venin !" WHERE entry = -10620;
UPDATE creature_ai_texts SET content_loc2 = "Par les profondeurs de Nazjatar !" WHERE entry = -10621;
UPDATE creature_ai_texts SET content_loc2 = "Vous m\'avez ramené trop tôt ! Je suis encore faible... Qu\'est-ce que c\'est ! Qui... ? Ça n\'a pas d\'importance. MOUREZ !" WHERE entry = -10622;
UPDATE creature_ai_texts SET content_loc2 = "Ma force... revient !" WHERE entry = -10623;
UPDATE creature_ai_texts SET content_loc2 = "Vous m\'avez guéri ! J\'ai une dette envers vous, $n." WHERE entry = -10624;
UPDATE creature_ai_texts SET content_loc2 = "Vous avez trouvé un remède ! Nous écraserons nos ennemis !" WHERE entry = -10625;
UPDATE creature_ai_texts SET content_loc2 = "Subissez ma colère, racaille |2 $r ! Vous ne vous en tirerez pas comme ça !" WHERE entry = -10626;
UPDATE creature_ai_texts SET content_loc2 = "Gardiens ! À moi !" WHERE entry = -10627;
UPDATE creature_ai_texts SET content_loc2 = "L\'%s s\'envole dans les feuillages, libéré de la corruption de Terokk." WHERE entry = -10628;
UPDATE creature_ai_texts SET content_loc2 = "Pour Lordaeron ! Pour la victoire !" WHERE entry = -10629;
UPDATE creature_ai_texts SET content_loc2 = "Boutez ces démons !" WHERE entry = -10630;
UPDATE creature_ai_texts SET content_loc2 = "Que la Lumière m\'accueille." WHERE entry = -10631;
UPDATE creature_ai_texts SET content_loc2 = "Écrasez-les !" WHERE entry = -10632;
UPDATE creature_ai_texts SET content_loc2 = "Pour la Terre-mère !" WHERE entry = -10633;
