UPDATE dbscript_string SET content_loc2 = "Il se passe quoi ?" WHERE entry = 2000000051;
UPDATE dbscript_string SET content_loc2 = "Coâ coâ ! Non ! Ce n\’est pas... Coâ coâ... possible ! Tu m\’as dupé... Coâ coâ... Tu n\’es qu\’un fourbe abject ! Coâ coâ !" WHERE entry = 2000000047;
UPDATE dbscript_string SET content_loc2 = "Maintenant plaçons cette terre si rare dans mon pot..." WHERE entry = 2000000048;
UPDATE dbscript_string SET content_loc2 = "Oooooo chaud chaud chaud ! Si cela ne vous revigore pas, je ne sais pas ce qui le fera." WHERE entry = 2000000046;
UPDATE dbscript_string SET content_loc2 = "Ouf ! Mon chapeau porte-bonheur est là. Je l\'ai cherché partout." WHERE entry = 2000005379;
UPDATE dbscript_string SET content_loc2 = "Je ne sais pas ce qui m\'a pris de sortir sans mon épée. Je l\'aurais emportée si je l\'avais vue ici..." WHERE entry = 2000005380;
UPDATE dbscript_string SET content_loc2 = "Il n\'est pas concevable que je sorte sans ma cape. J\'espère qu\'elle est ici..." WHERE entry = 2000005381;
UPDATE dbscript_string SET content_loc2 = "La voilà ! J\'aurais pourtant juré qu\'elle n\'y était pas la dernière fois que j\'ai regardé..." WHERE entry = 2000005382;
UPDATE dbscript_string SET content_loc2 = "Je n\'arrive pas à m\'endormir si on ne me raconte pas une belle histoire. Maintenant, je suis sûre de bien me reposer." WHERE entry = 2000005383;
UPDATE dbscript_string SET content_loc2 = "C\’est fait." WHERE entry = 2000000044;
UPDATE dbscript_string SET content_loc2 = "Un thé brûlant, il n\'y a rien de tel pour bien commencer la journée lorsque l\'air est frais en Dun Morogh !" WHERE entry = 2000000045;
UPDATE dbscript_string SET content_loc2 = "À la prochaine, mon brave." WHERE entry = 2000000042;
UPDATE dbscript_string SET content_loc2 = "Vous pensiez donc faire passer Mor\'ghor pour un imbécile, hein ? Avant d\'être $glivré:livrée; au seigneur Illidan, vous allez découvrir des douleurs dont vous ne soupçonniez pas l\'existence. Je vais prendre plaisir à accomplir ma propre vengeance." WHERE entry = 2000000041;
UPDATE dbscript_string SET content_loc2 = "Ce sera fait, seigneur." WHERE entry = 2000000040;
UPDATE dbscript_string SET content_loc2 = "Mais... Seigneur, je ne comprends pas. $n... C\'est l\'orc qui a..." WHERE entry = 2000000039;
UPDATE dbscript_string SET content_loc2 = "Seigneur Illidan, voici le Gueule-de-dragon dont nous vous avons parlé. Il nous conduira à la victoire !" WHERE entry = 2000000038;
UPDATE dbscript_string SET content_loc2 = "Gloire au seigneur Illidan !" WHERE entry = 2000000037;
UPDATE dbscript_string SET content_loc2 = "Le seigneur Illidan sera bientôt là." WHERE entry = 2000000036;
UPDATE dbscript_string SET content_loc2 = "Guerriers de la Gueule-de-dragon, approchez ! L\'un d\'entre vous a atteint le grade de généralissime ! Inclinez-vous bien bas ! Présentez votre respect et votre allégeance au généralissime $n !" WHERE entry = 2000000035;
UPDATE dbscript_string SET content_loc2 = "Je suis un chevalier de sang, bâtard. Je ne sers pas la Lumière, la Lumière me sert. Si quelqu\’un doit faire preuve de pitié, c\’est moi et moi seul." WHERE entry = 2000000890;
UPDATE dbscript_string SET content_loc2 = "Venez, $n. Le seigneur Hurlorage attend." WHERE entry = 2000000034;
UPDATE dbscript_string SET content_loc2 = "Tenez-vous sur vos gardes, $n, car le démon dans cet orbe est une gangrebête." WHERE entry = 2000000032;
UPDATE dbscript_string SET content_loc2 = "Votre corps est à moi ! Nos destins sont entrelacés à jamais." WHERE entry = 2000000031;
UPDATE dbscript_string SET content_loc2 = "Oups !" WHERE entry = 2000000026;
UPDATE dbscript_string SET content_loc2 = "Bon, voyons... Si je ne me trompe pas... Heu..." WHERE entry = 2000000540;
UPDATE dbscript_string SET content_loc2 = "Un avatar de Terokk ! Aux armes !" WHERE entry = 2000000025;
UPDATE dbscript_string SET content_loc2 = "Prenez patience, $n. La torche est presque consumée." WHERE entry = 2000000019;
UPDATE dbscript_string SET content_loc2 = "Hé, qui c\’est que Fengus traite de fils de gnoll ? Amenez les prisonniers au roi. C\'t\'une bonne idée d\'les amener avec leurs armes, que l\'roi, y voie bien qu\’y sont dangereux. Moi, j\’vais voir si Fengus ouvre encore sa grand\' gueule quand j\’la lui aurai cassée menu ! Har !" WHERE entry = 2000000268;
UPDATE dbscript_string SET content_loc2 = "%s commence à manipuler la Pierre de Relu au-dessus des deux fossiles." WHERE entry = 2000005568;
UPDATE dbscript_string SET content_loc2 = "Par les étoiles ! Un esprit a été invoqué !" WHERE entry = 2000005569;
UPDATE dbscript_string SET content_loc2 = "$n a battu Brisorteil !" WHERE entry = 2000000003;
UPDATE dbscript_string SET content_loc2 = "Entrez dans l\'arène de sang, $n. Le combat va commencer !" WHERE entry = 2000000001;
UPDATE dbscript_string SET content_loc2 = "Le combat est sur le point de commencer ! $n contre le féroce Sabot-fourchu Brisorteil !" WHERE entry = 2000000002;
UPDATE dbscript_string SET content_loc2 = "Les exportations d\'Heraazi sont en augmentation, maître. Nous avons d\'ores et déjà soudoyé les officiers taxateurs, et les marchandises de contrebande commenceront à arriver dans quelques jours." WHERE entry = 2000005363;
UPDATE dbscript_string SET content_loc2 = "Cela n\'a rien de très surprenant. Revenez quand vous aurez de vraies nouvelles, merci." WHERE entry = 2000005364;
UPDATE dbscript_string SET content_loc2 = "Comme prévu. Très bien, disposez." WHERE entry = 2000005365;
UPDATE dbscript_string SET content_loc2 = "Avec votre permission, maître..." WHERE entry = 2000005366;
UPDATE dbscript_string SET content_loc2 = "Les voilà !" WHERE entry = 2000005765;
UPDATE dbscript_string SET content_loc2 = "Suivez mes instructions, $n." WHERE entry = 2000005648;
UPDATE dbscript_string SET content_loc2 = "Pourquoi m\’avez-vous invoquée si tôt ? Je ne dispose pas encore de tous mes pouvoirs !" WHERE entry = 2000000235;
UPDATE dbscript_string SET content_loc2 = "Peu importe. Vous avez été assez stupide pour m\’aider et maintenant vous allez payer !" WHERE entry = 2000000236;
UPDATE dbscript_string SET content_loc2 = "La bête est morte ! Tout va bien dans Sombre-Comté !" WHERE entry = 2000000188;
UPDATE dbscript_string SET content_loc2 = "Comment ? $n, vous m\’avez été bien utile, cependant..." WHERE entry = 2000000234;
UPDATE dbscript_string SET content_loc2 = "L\’abomination a envahi le camp des Veilleurs ! Vite, nous devons l\’intercepter avant qu\’elle n\’atteigne la ville !" WHERE entry = 2000000187;
UPDATE dbscript_string SET content_loc2 = "Bonjour, Selarin. Je crains de ne pas pouvoir vous aider pour le moment, mais peut-être pouvez-vous vous adresser à certains aventuriers qui séjournent à Auberdine..." WHERE entry = 2000000797;
UPDATE dbscript_string SET content_loc2 = "Dans les boîtes, mes jolis ! Un par un..." WHERE entry = 2000000202;
UPDATE dbscript_string SET content_loc2 = "Merci, Terenthis. Je resterai ici aussi longtemps que je pourrai." WHERE entry = 2000000798;
UPDATE dbscript_string SET content_loc2 = "Espérons que je puisse envoyer quelques braves pour aider Orneval et les Sentinelles." WHERE entry = 2000000799;
UPDATE dbscript_string SET content_loc2 = "Parlez-moi pour écouter mon histoire." WHERE entry = 2000005241;
UPDATE dbscript_string SET content_loc2 = "Merci, $r ! Je suis sûr que mon papa vous donnera une très belle récompense ! Salut !" WHERE entry = 2000000821;
UPDATE dbscript_string SET content_loc2 = "C\'est la dernière fois que je me fais attraper ! Promis ! Salut !" WHERE entry = 2000000822;
UPDATE dbscript_string SET content_loc2 = "Merci, $n ! Ça fait combien de fois que vous me sauvez la vie ? Trois ? Pour moi, c\'est comme si on était du même sang !" WHERE entry = 2000000503;
UPDATE dbscript_string SET content_loc2 = "Donnez un peu de votre cœur et de votre bourse ! Donnez aux enfants de Hurlevent qui ont perdu leurs parents." WHERE entry = 2000000351;
UPDATE dbscript_string SET content_loc2 = "Aidez les enfants de Hurlevent... victimes de la guerre et de la peste !" WHERE entry = 2000000352;
UPDATE dbscript_string SET content_loc2 = "Donnez aux œuvres de bienfaisance qui essaient d\'aider les victimes de ces temps difficiles ! S\'il vous plaît." WHERE entry = 2000000349;
UPDATE dbscript_string SET content_loc2 = "N\'oubliez pas les orphelins de Hurlevent !" WHERE entry = 2000000350;
UPDATE dbscript_string SET content_loc2 = "D\’accord ! Zoziaux font comme ils veulent ! Mug\’gok va chercher des nouveaux amis !" WHERE entry = 2000000394;
UPDATE dbscript_string SET content_loc2 = "%s examine attentivement le grand vase." WHERE entry = 2000005348;
UPDATE dbscript_string SET content_loc2 = "Pauvre Dorius. Si jamais je mets la main sur ces Sombrefer, je vous jure que..." WHERE entry = 2000005349;
UPDATE dbscript_string SET content_loc2 = "Sortez de là, petits zoziaux ! Mug\’gok veut jouer !" WHERE entry = 2000000392;
UPDATE dbscript_string SET content_loc2 = "Hommes-oiseaux pas rendre Mug\'gok triste !" WHERE entry = 2000000393;
UPDATE dbscript_string SET content_loc2 = "Maçons... euuhhh... Défias, soyez sur vos gardes : ce soir, l\’ancre rouillée coulera." WHERE entry = 2000005407;
UPDATE dbscript_string SET content_loc2 = "Le chef veut tout le monde à son poste. Ce soir, l\’ancre rouillée coulera." WHERE entry = 2000005408;
UPDATE dbscript_string SET content_loc2 = "Le prêtre s\'agenouille et prie silencieusement." WHERE entry = 2000005409;
UPDATE dbscript_string SET content_loc2 = "Lumière, donne-moi la force." WHERE entry = 2000005410;
UPDATE dbscript_string SET content_loc2 = "Lumière, conduis les défunts de l\'illusion vers la vérité, de l\'ombre vers la Lumière et de la mort à l\'immortalité." WHERE entry = 2000005411;
UPDATE dbscript_string SET content_loc2 = "Où allez-vous comme ça, soldat ? Vos blessures pourraient être mortelles. Retournez dans cette tente et laissez l\'infirmière s\'occuper de vous !" WHERE entry = 2000005412;
UPDATE dbscript_string SET content_loc2 = "Alors laissez-moi passer, il est temps de renvoyer la Légion ardente au Néant !" WHERE entry = 2000005413;
UPDATE dbscript_string SET content_loc2 = "Sniff... Bel esprit, soldat... J\'aimerais bien qu\'on en ait plus, des comme vous, dans cette armée. Allez-y, et faites-leur en baver !!" WHERE entry = 2000005414;
UPDATE dbscript_string SET content_loc2 = "NOOOOON!!!" WHERE entry = 2000005415;
UPDATE dbscript_string SET content_loc2 = "Mes amis... Je vous ai laissé tomber..." WHERE entry = 2000005416;
UPDATE dbscript_string SET content_loc2 = "Non, mon ami... c\'est nous... c\'est nous qui vous laissons tomber." WHERE entry = 2000005417;
UPDATE dbscript_string SET content_loc2 = "Merci, $r ! Le service que vous avez rendu aux Mag\'har ne sera pas oublié." WHERE entry = 2000005428;
UPDATE dbscript_string SET content_loc2 = "Merci mille fois, héros !" WHERE entry = 2000005429;
UPDATE dbscript_string SET content_loc2 = "Les esprits vous protègent, $r !" WHERE entry = 2000005430;
UPDATE dbscript_string SET content_loc2 = "Mort aux ogres !" WHERE entry = 2000005431;
UPDATE dbscript_string SET content_loc2 = "Merci. Je n\'aurais jamais dû quitter la forêt des Pins-Argentés." WHERE entry = 2000005543;
UPDATE dbscript_string SET content_loc2 = "Vous avez toute ma gratitude !" WHERE entry = 2000005544;
UPDATE dbscript_string SET content_loc2 = "M... merci... J\'ai cru mourir." WHERE entry = 2000005545;
UPDATE dbscript_string SET content_loc2 = "%s éclate en deux !" WHERE entry = 2000000164;
UPDATE dbscript_string SET content_loc2 = "Nous devons donc en finir avec Athrikus Narassin. Dites-moi, esprit, où pouvons-nous trouver les autres gemmes d\'âme pour défaire ce sortilège ?" WHERE entry = 2000000165;
UPDATE dbscript_string SET content_loc2 = "Pendant la Fracture, Ameth\’Aran était à la merci de la terreur qui avait saisi le pays. L\’espoir de survie était faible." WHERE entry = 2000000167;
UPDATE dbscript_string SET content_loc2 = "Qui ose entrer dans mon domaine ? Venez ! Venez et soyez consumé !" WHERE entry = 2000005552;
UPDATE dbscript_string SET content_loc2 = "Sans moi !" WHERE entry = 2000005551;
UPDATE dbscript_string SET content_loc2 = "Vraiment ?" WHERE entry = 2000001908;
UPDATE dbscript_string SET content_loc2 = "Tu sais qu\’il y a des crocilisques dans les canaux. Les gens les ont capturés dans les marais pour s\’en faire des animaux de compagnie et ont fini par les relâcher dans les canaux." WHERE entry = 2000001907;
UPDATE dbscript_string SET content_loc2 = "Et alors le lapin l\’a mordu et lui a arraché la tête... Je le jure." WHERE entry = 2000001906;
UPDATE dbscript_string SET content_loc2 = "Il n\’y a pas de cuillère." WHERE entry = 2000001904;
UPDATE dbscript_string SET content_loc2 = "Je le jure, il y a des gens qui les ont vus. Les pandaren existent !" WHERE entry = 2000001905;
UPDATE dbscript_string SET content_loc2 = "On dit qu\’il se métamorphose parfois en corbeau." WHERE entry = 2000001902;
UPDATE dbscript_string SET content_loc2 = "Tu sais pourquoi les orcs ont des yeux rougeoyants ? Parce qu\’ils boivent du sang !" WHERE entry = 2000001903;
UPDATE dbscript_string SET content_loc2 = "Comme vous voulez..." WHERE entry = 2000000195;
UPDATE dbscript_string SET content_loc2 = "Hâtez-vous, $n. Mon destin est entre vos mains." WHERE entry = 2000005089;
UPDATE dbscript_string SET content_loc2 = "Je vous remercie, $n. Mon frère également." WHERE entry = 2000005776;
UPDATE dbscript_string SET content_loc2 = "Quoi ? La Lame ardente s\'étend !? Nous devons enquêter !" WHERE entry = 2000005778;
UPDATE dbscript_string SET content_loc2 = "%s écoute le rapport |2 $n..." WHERE entry = 2000005777;
UPDATE dbscript_string SET content_loc2 = "Les chevaliers firent face aux charges de la Horde et résistèrent à un millier d\’orcs enragés qui déferlaient à travers la vallée." WHERE entry = 2000001901;
UPDATE dbscript_string SET content_loc2 = "Et maintenant, silence ! Si vous oubliez encore où vous vous trouvez, j\’informerai le seigneur Fordring et ses chevaliers que votre présence ici n\’est plus souhaitable. Je suis sûr qu\’il prendrait plaisir à votre « pénitence »." WHERE entry = 2000009095;
UPDATE dbscript_string SET content_loc2 = "Je saurai utiliser le temps à bon escient. Merci." WHERE entry = 2000010177;
UPDATE dbscript_string SET content_loc2 = "%s ramasse le drapeau naga." WHERE entry = 2000000297;
UPDATE dbscript_string SET content_loc2 = "C\’est quoi, la grande idée, l\’Étincelle ? Pourquoi avoir demandé cette réunion ?" WHERE entry = 2000000298;
UPDATE dbscript_string SET content_loc2 = "La grande idée, c\’est d\’éviter de griller ma couverture, bougre d\’idiot ! Je t\’avais dit de mettre le compas et les cartes marines à l\’abri, pas de les laisser traîner là où n\’importe quel crétin pouvait les trouver." WHERE entry = 2000000299;
UPDATE dbscript_string SET content_loc2 = "Le Maître a dû consentir à de très gros efforts pour obtenir ces informations sur l\’emplacement de l\’Exodar. Tu as failli compromettre toute l\’opération, y compris la couverture de notre espion de l\’intérieur." WHERE entry = 2000000300;
UPDATE dbscript_string SET content_loc2 = "Allez, l\’Étincelle, on se détend ! Tout est sous contrôle. Nous allons dépouiller l\’Exodar sous leur nez, et devenir très, très riches, tous les deux." WHERE entry = 2000000301;
UPDATE dbscript_string SET content_loc2 = "Me détendre ? Tu sais ce que Kael\'thas fait à ceux qui échouent, Geezle ? Souffrances et douleurs éternelles... Faut PAS que tu te plantes, crétin." WHERE entry = 2000000302;
UPDATE dbscript_string SET content_loc2 = "Nos éclaireurs à Brume-sang ont trouvé notre contact. Cet imbécile de Velen se trouvera bientôt sans défense... Assez longtemps pour nous permettre de frapper ! Maintenant, hors de ma vue avant que je te vaporise..." WHERE entry = 2000000303;
UPDATE dbscript_string SET content_loc2 = "Oui, monsieur. Ça n\'arrivera plus..." WHERE entry = 2000000304;
UPDATE dbscript_string SET content_loc2 = "Ne perdez pas votre temps, imbécile ! Usez de notre puissance pour tuer ceux qui se dressent contre nous ! Détruisez Karsius avant de tomber sous son contrôle !" WHERE entry = 2000005473;
UPDATE dbscript_string SET content_loc2 = "Nous nous retrouverons... Un jour." WHERE entry = 2000005471;
UPDATE dbscript_string SET content_loc2 = "La mort incarnée est à nouveau lâchée sur le monde ! Teron Fielsang est de retour..." WHERE entry = 2000005472;
UPDATE dbscript_string SET content_loc2 = "... la création d\’un fonds consacré à l\’achat de gel pour les cheveux ? Ma coiffure me convient, merci bien !" WHERE entry = 2000005511;
UPDATE dbscript_string SET content_loc2 = "... en tant que déserteur, vous aurez le plaisir de participer à des activités comme la nage attaché et la toilette des chiens ? Mais c\’est ridicule !" WHERE entry = 2000005512;
UPDATE dbscript_string SET content_loc2 = "Je ne risque pas de vous rejoindre. Mais je garde votre machin, il me sera très utile aux toilettes..." WHERE entry = 2000005513;
UPDATE dbscript_string SET content_loc2 = "... l\’objectif des déserteurs est une Theramore où tous les citoyens seraient libres de jongler à toute heure du jour et de la nuit ? Il n\’y a pas une coquille quelque part ?" WHERE entry = 2000005514;
UPDATE dbscript_string SET content_loc2 = "Je n\'avais rien lu d\'aussi drôle depuis des mois. Merci, vous avez ensoleillé ma journée !" WHERE entry = 2000005515;
UPDATE dbscript_string SET content_loc2 = "Non mais... c\'est une blague ?" WHERE entry = 2000005516;
UPDATE dbscript_string SET content_loc2 = "Merci." WHERE entry = 2000005517;
UPDATE dbscript_string SET content_loc2 = "%s lit le pamphlet." WHERE entry = 2000005518;
UPDATE dbscript_string SET content_loc2 = "... comment ça, cinquante pour cent de remise sur les caleçons de déserteur ? C\’est quoi, ces âneries ?" WHERE entry = 2000005519;
UPDATE dbscript_string SET content_loc2 = "Quelle bande de cinglés ! Vous croyez vraiment à ces trucs ?" WHERE entry = 2000005520;
UPDATE dbscript_string SET content_loc2 = "Tout le monde aux postes de combat ! Les nagas arrivent !" WHERE entry = 2000005632;
UPDATE dbscript_string SET content_loc2 = "Si nous parvenons à les retenir maintenant, je suis sûr que nous serons hors de danger." WHERE entry = 2000005633;
UPDATE dbscript_string SET content_loc2 = "Et pourtant nous avons fini par nous retourner contre nous-mêmes. Les frères s\'affrontaient entre eux pour savoir qui était le plus fort." WHERE entry = 2000001580;
UPDATE dbscript_string SET content_loc2 = "Longtemps, nous nous sommes bien battus. Nous avons montré encore et encore qu\'il fallait compter avec la puissance des ogres." WHERE entry = 2000001579;
UPDATE dbscript_string SET content_loc2 = "Nous étions forts, mais nous ne connaissions rien à part la violence..." WHERE entry = 2000001578;
UPDATE dbscript_string SET content_loc2 = "Autrefois, nous les ogres étions perdus..." WHERE entry = 2000001577;
UPDATE dbscript_string SET content_loc2 = "Tu as trouvé un truc rigolo à chasser, Espiègle ? Je parie que oui !" WHERE entry = 2000000088;
UPDATE dbscript_string SET content_loc2 = "C\’est juste toi et moi, Espiègle. Papa ne va plus rentrer à la maison." WHERE entry = 2000000089;
UPDATE dbscript_string SET content_loc2 = "Je me demande à quelle distance ça enverrait ce petit chat blanc..." WHERE entry = 2000000090;
UPDATE dbscript_string SET content_loc2 = "Mon peuple a survécu en Outreterre..." WHERE entry = 2000000016;
UPDATE dbscript_string SET content_loc2 = "Alors, les réparations..." WHERE entry = 2000005754;
UPDATE dbscript_string SET content_loc2 = "... sans se faire corrompre !" WHERE entry = 2000000018;
UPDATE dbscript_string SET content_loc2 = "Thrall ! Mon seigneur... tout va bien ?" WHERE entry = 2000000098;
UPDATE dbscript_string SET content_loc2 = "Ça va aller, Eitrigg. Nous devons commencer les préparatifs... Il faut que je parte pour l\'Outreterre immédiatement !" WHERE entry = 2000000099;
UPDATE dbscript_string SET content_loc2 = "Avec tout le respect que je vous dois, chef, nos ennemis risquent de tirer profit de votre soudaine absence si nous ne nous y préparons pas correctement." WHERE entry = 2000000100;
UPDATE dbscript_string SET content_loc2 = "Vous avez raison, Eitrigg. Le moment est mal choisi. Assurez-vous que nos meilleurs ambassadeurs soient choisis pour nous représenter auprès de Hurlenfer." WHERE entry = 2000000101;
UPDATE dbscript_string SET content_loc2 = "Tout de suite, chef ! Pour la Horde !" WHERE entry = 2000000102;
UPDATE dbscript_string SET content_loc2 = "S\’il est un tant soit peu comme son père, une alliance avec les Mag\'har marquera l\’aube d\’une nouvelle ère pour la Horde." WHERE entry = 2000000103;
UPDATE dbscript_string SET content_loc2 = "Rien ne se passe !" WHERE entry = 2000000135;
UPDATE dbscript_string SET content_loc2 = "Si vous croyez que je vais trahir aussi facilement mon ami, vous vous trompez ! Battez-vous, canaille !" WHERE entry = 2000000253;
UPDATE dbscript_string SET content_loc2 = "... une décoction puissante !" WHERE entry = 2000000254;
UPDATE dbscript_string SET content_loc2 = "Où diable pourrais-je trouver un cobaye adéquat ?" WHERE entry = 2000000255;
UPDATE dbscript_string SET content_loc2 = "Bois ça, grosse brute bleue ! Désobéis-moi et je te promets une mort lente et douloureuse !" WHERE entry = 2000000256;
UPDATE dbscript_string SET content_loc2 = "Je vous plains, Réprouvés. La lumière est absente de vos âmes." WHERE entry = 2000000257;
UPDATE dbscript_string SET content_loc2 = "Hummm... On dirait bien que je vais avoir besoin d\'un nouveau cobaye." WHERE entry = 2000000258;
UPDATE dbscript_string SET content_loc2 = "Et qu\'est-ce que vous imaginez, au juste ? Vous osez déranger l\'expérience de mon maître ?" WHERE entry = 2000000369;
UPDATE dbscript_string SET content_loc2 = "Vous appréciez notre travail ici ? Peut-être ferons-nous tomber ces cristaux du ciel sur toute l\'Outreterre." WHERE entry = 2000000370;
UPDATE dbscript_string SET content_loc2 = "Vos tentatives de guérir cette terre et d\'endiguer les énergies qui invoquent les colosses et les rendent fous commencent à m\'ennuyer profondément. Goliathon, roi des colosses, Kael\'thas et moi-même t\'ordonnons de défendre le cristal !" WHERE entry = 2000000371;
UPDATE dbscript_string SET content_loc2 = "Nous nous reverrons bientôt." WHERE entry = 2000000372;
UPDATE dbscript_string SET content_loc2 = "Terenthis, Raene m\’envoie pour te trouver. Les Sentinelles ont besoin d\’aide en Orneval." WHERE entry = 2000000260;
UPDATE dbscript_string SET content_loc2 = "Fouillez ces gravats." WHERE entry = 2000000004;
UPDATE dbscript_string SET content_loc2 = "Trouvez-moi quelque chose d\’intéressant, chiens !" WHERE entry = 2000000005;
UPDATE dbscript_string SET content_loc2 = "Si vous êtes ici, cela signifie que vous êtes $gprêt:prête; à entamer une étude sur l\’invocation des cohortes démoniaques en vue de réaliser votre union. Nous commencerons par les créatures les plus humbles que vous serez en mesure d\’invoquer puis nous continuerons. Commençons." WHERE entry = 2000005634;
UPDATE dbscript_string SET content_loc2 = "La créature que vous pourrez invoquer sans aucune difficulté est le diablotin. Vous devriez déjà être en mesure de créer ce serviteur, mais afin de vous délivrer un enseignement complet je vais commencer par lui." WHERE entry = 2000005635;
UPDATE dbscript_string SET content_loc2 = "Cette ignoble petite créature est le diablotin. Il est petit et faible, et donc quasiment inutile en tant que bouclier de chair, et sa capacité à infliger des dégâts est au mieux médiocre. Il vaut mieux utiliser cette créature pour soutenir un groupe assez grand." WHERE entry = 2000005636;
UPDATE dbscript_string SET content_loc2 = "Maintenant que vous avez eu la chance d\’étudier un diablotin, passons au prochain serviteur que vous serez en mesure d\’invoquer, le marcheur du Vide." WHERE entry = 2000005637;
UPDATE dbscript_string SET content_loc2 = "Cette entité démoniaque est connue sous le nom de \"marcheur du Vide\". Sa force et son endurance, qui ne sont pas à négliger, sont parfaitement adaptées à la défense. Envoyez-la attaquer vos ennemis, puis utilisez-la comme un bouclier pendant que vous employez vos sorts et vos techniques pour drainer la vie hors du corps de votre adversaire." WHERE entry = 2000005638;
UPDATE dbscript_string SET content_loc2 = "Si vous n\’en avez jamais vu, c\’est un spectacle à voir. Une créature très impressionnante, à la fois sur le champ de bataille et en dehors. À présent, jetons un œil à celle que tous les étudiants mâles attendent. La succube." WHERE entry = 2000005639;
UPDATE dbscript_string SET content_loc2 = "Voyons. Mis à part les diversions évidentes qu\'une telle servante occasionnera envers vos ennemis mâles, elle est également capable de causer des dommages d\’une ampleur impressionnante. Cependant, sa faible résistance la rend quasiment inutile en tant que bouclier." WHERE entry = 2000005640;
UPDATE dbscript_string SET content_loc2 = "Étudiez assidûment, et un jour vous pourriez être capable d\’en invoquer une par vous-même. Mais pour le moment, passons au chasseur corrompu." WHERE entry = 2000005641;
UPDATE dbscript_string SET content_loc2 = "Ce que vous voyez devant vous est un chasseur corrompu. Les talents naturels de cette créature lui permettent de neutraliser des sorts. Ce talent, ainsi que d\’autres capacités, la rendent sans égale face à un adversaires doté de pouvoirs magiques." WHERE entry = 2000005642;
UPDATE dbscript_string SET content_loc2 = "Lorsque vous êtes face à un lanceur de sorts quelconque, cette bête sauvage est votre meilleur ami. Maintenant, intéressons-nous à quelque chose d\'un peu différent. La créature suivante vous aidera à voyager et facilitera grandement vos futurs déplacements. Jetons un coup d\'œil sur un palefroi corrompu." WHERE entry = 2000005643;
UPDATE dbscript_string SET content_loc2 = "Je ne pense pas que vous ayez eu de nombreuses occasions de voir une telle créature. Ces chevaux-démons rendront vos voyages beaucoup plus rapides si vous les utilisez comme montures. Du moins tant que vous pourrez les contrôler, ce qui n\'est pas facile. Avant de faire une tentative, soyez sûrs d\'être prêts." WHERE entry = 2000005644;
UPDATE dbscript_string SET content_loc2 = "Voilà. Votre leçon sur l\’invocation est terminée. Un nouveau cours est sur le point de commencer, alors si vous souhaitez réviser, n\'hésitez pas à rester." WHERE entry = 2000005645;
UPDATE dbscript_string SET content_loc2 = "Cénarius combat à mes côtés. Illidan est assis sur son trône d\'Outreterre, ruminant ses pensées. Je crains que sa défaite face à Arthas n\'ait été plus qu\'il ne pouvait supporter. La folie s\'est emparée de lui, Remulos. Il rejoue ces événements dans son esprit un millier de fois chaque jour, sauf que dans son esprit, il triomphe et Arthas est vaincu à jamais. Il est parti trop loin, mon vieil ami. Je crains que vienne rapidement le moment où notre lien sera mis à l\’épreuve, et cela ne se passera pas comme au Puits de Zin-Azshari." WHERE entry = 2000000384;
UPDATE dbscript_string SET content_loc2 = "Vous êtes parti trop longtemps, Malfurion. La paix entre les enfants d\'Azeroth est au mieux précaire. Qu\'en est-il de mon père ? De votre frère ? Avez-vous des nouvelles ?" WHERE entry = 2000000383;
UPDATE dbscript_string SET content_loc2 = "Oui, Remulos. Préparez les races mortelles." WHERE entry = 2000000382;
UPDATE dbscript_string SET content_loc2 = "Je le sens aussi, Malfurion. Des jours sombres nous attendent." WHERE entry = 2000000381;
UPDATE dbscript_string SET content_loc2 = "Remulos, mon vieil ami. Quelle joie de vous revoir. Je savais que le message vous parviendrait, d\'une façon ou d\'une autre." WHERE entry = 2000000378;
UPDATE dbscript_string SET content_loc2 = "Il était voilé de cauchemars, Malfurion. Que se passe-t-il dans le rêve ? Qu\'est-ce qui pourrait causer de telles atrocités ?" WHERE entry = 2000000379;
UPDATE dbscript_string SET content_loc2 = "Je crains le pire, mon vieil ami. À l\'intérieur du Rêve, nous affrontons un nouvel ennemi, né d\'un mal ancien. La noble progéniture d\'Ysera est devenue la victime des murmures anciens. On dirait que le Cauchemar a pénétré dans le royaume et cherche un nouvel hôte dans Azeroth." WHERE entry = 2000000380;
UPDATE dbscript_string SET content_loc2 = "Restez près de moi, $n. Je vous protégerai si quelque chose devait mal se passer." WHERE entry = 2000000376;
UPDATE dbscript_string SET content_loc2 = "Malfurion !" WHERE entry = 2000000377;
UPDATE dbscript_string SET content_loc2 = "Venez, $n. Le lac est tout proche." WHERE entry = 2000000375;
UPDATE dbscript_string SET content_loc2 = "Ok, allons-y. Je connais justement un endroit idéal !" WHERE entry = 2000000388;
UPDATE dbscript_string SET content_loc2 = "Quoi... Qu\’est-ce qui... m\’arrive ? Sortez ce chat de ma vue !" WHERE entry = 2000000389;
UPDATE dbscript_string SET content_loc2 = "Espiègle regarde Viera Murmesoleil avec des yeux affamés." WHERE entry = 2000000390;
UPDATE dbscript_string SET content_loc2 = "Viera Murmesoleil file comme une dératée pour échapper à Espiègle." WHERE entry = 2000000391;
UPDATE dbscript_string SET content_loc2 = "%s commence à canaliser son énergie, en se concentrant sur la pierre." WHERE entry = 2000005650;
UPDATE dbscript_string SET content_loc2 = "Ensemble, les deux morceaux ne feront qu\'un à nouveau." WHERE entry = 2000005651;
UPDATE dbscript_string SET content_loc2 = "Shal myrinan ishnu daldorah..." WHERE entry = 2000005652;
UPDATE dbscript_string SET content_loc2 = "Mon sceptre sera de nouveau unifié !" WHERE entry = 2000005653;
UPDATE dbscript_string SET content_loc2 = "J\'espère que c\'était un poisson !" WHERE entry = 2000001921;
UPDATE dbscript_string SET content_loc2 = "Je doute fort qu\’il ait quelque chose à dire, $gétranger:étrangère; ..." WHERE entry = 2000000455;
UPDATE dbscript_string SET content_loc2 = "Ce serait vraiment dommage de laisser perdre ces pamphlets, n\'est-ce pas ?" WHERE entry = 2000000464;
UPDATE dbscript_string SET content_loc2 = "Moyennant quelques corrections créatives, ils pourraient devenir très utiles..." WHERE entry = 2000000465;
UPDATE dbscript_string SET content_loc2 = "Préparez-vous ! Les nagas ne tarderont pas à pousser leur avantage quand ils sauront que nous sommes en vie." WHERE entry = 2000000466;
UPDATE dbscript_string SET content_loc2 = "%s se réveille en sursaut." WHERE entry = 2000000467;
UPDATE dbscript_string SET content_loc2 = "Ce... C\'était un rêve ? Brume-azur, Brume-sang, le marécage de Zangar, Ombrelune... Et même Zul\'Aman... Jane ! Jane, où es-tu ?!" WHERE entry = 2000000468;
UPDATE dbscript_string SET content_loc2 = "Je suis là, Papa. Tu as dormi toute la journée. Tu devais vraiment être fatigué !" WHERE entry = 2000000469;
UPDATE dbscript_string SET content_loc2 = "Ah, heureusement, tout cela n\'était qu\'un rêve..." WHERE entry = 2000000470;
UPDATE dbscript_string SET content_loc2 = "%s retourne à ses jeux sur la plage." WHERE entry = 2000000471;
UPDATE dbscript_string SET content_loc2 = "Erk ! Chaque fois que je charge quelque chose dans le chariot, ça réapparaît ici après quelques instants !" WHERE entry = 2000000472;
UPDATE dbscript_string SET content_loc2 = "J\'aurais dû savoir que ce n\'était pas très malin de donner ce grimoire à Silanna." WHERE entry = 2000000473;
UPDATE dbscript_string SET content_loc2 = "%s range les bouteilles et pousse un soupir." WHERE entry = 2000000474;
UPDATE dbscript_string SET content_loc2 = "Nos vies sont à vous, chevalier de sang." WHERE entry = 2000000475;
UPDATE dbscript_string SET content_loc2 = "Même la Lumière ne peut égaler la volonté indomptable de notre peuple." WHERE entry = 2000000476;
UPDATE dbscript_string SET content_loc2 = "Votre puissance fait notre force à tous." WHERE entry = 2000000477;
UPDATE dbscript_string SET content_loc2 = "%s regarde les gardes tour à tour." WHERE entry = 2000000478;
UPDATE dbscript_string SET content_loc2 = "%s examine le petit terrain d\’entraînement des forestiers avec beaucoup de condescendance." WHERE entry = 2000000479;
UPDATE dbscript_string SET content_loc2 = "%s salue les deux gardiens en inclinant légèrement la tête." WHERE entry = 2000000480;
UPDATE dbscript_string SET content_loc2 = "%s lève un sourcil parfait et examine les gardes d\’un air très désapprobateur." WHERE entry = 2000000481;
UPDATE dbscript_string SET content_loc2 = "Quel joli coup !" WHERE entry = 2000000482;
UPDATE dbscript_string SET content_loc2 = "Ha ! En plein dans le mille !" WHERE entry = 2000000483;
UPDATE dbscript_string SET content_loc2 = "Agh... on vient de marcher sur ma tombe." WHERE entry = 2000000484;
UPDATE dbscript_string SET content_loc2 = "Ils verront la lumière, j\'espère." WHERE entry = 2000000485;
UPDATE dbscript_string SET content_loc2 = "Je pense que je peux aller jusqu\'à Mur-de-Fougères seule. La cargaison est en haut de la tour. Soyez $gprudent:prudente; !" WHERE entry = 2000000486;
UPDATE dbscript_string SET content_loc2 = "Aux armes ! La prisonnière s\'échappe !" WHERE entry = 2000000487;
UPDATE dbscript_string SET content_loc2 = "%s lit le Livre des morts." WHERE entry = 2000000488;
UPDATE dbscript_string SET content_loc2 = "Shane Dabiri, Lee, Alex T., Rob F., J. A. B., A. Mayberry !" WHERE entry = 2000000489;
UPDATE dbscript_string SET content_loc2 = "Jeffrey Kaplan ! Tom Chilton ! Rob Pardo !" WHERE entry = 2000000490;
UPDATE dbscript_string SET content_loc2 = "Justin Thavirat ! Roman Kenney ! Tout notre terrible équipe d\'animateurs et de graphistes !" WHERE entry = 2000000491;
UPDATE dbscript_string SET content_loc2 = "Kevin Jordan et Jonathan LeCraft !" WHERE entry = 2000000492;
UPDATE dbscript_string SET content_loc2 = "Essayez de limiter les erreurs d\'estimation au maximum. Je détesterais devoir expliquer à Gezhe que nous avons encore surpayé une livraison de gemmes ce mois-ci." WHERE entry = 2000000493;
UPDATE dbscript_string SET content_loc2 = "Oh mon dieu, j\'ai cru que j\'étais mort. Beuh... j\'ai encore la tête qui tourne..." WHERE entry = 2000000519;
UPDATE dbscript_string SET content_loc2 = "Je peux rentrer seul au bastion, je pense. Maintenant que vous m\’avez fait gagner un peu de temps, je devrais pouvoir sortir d\’ici discrètement. Qui que vous soyez, merci. Puisse Élune toujours vous bénir !" WHERE entry = 2000000520;
UPDATE dbscript_string SET content_loc2 = "Je vais devoir focaliser mes énergies sur ce couteau. En le faisant, je devrais récupérer un peu des puissantes impressions psychiques qui y sont associées." WHERE entry = 2000000521;
UPDATE dbscript_string SET content_loc2 = "Je... J\'en ai vu assez... Si épuisant..." WHERE entry = 2000000522;
UPDATE dbscript_string SET content_loc2 = "Féralas : Une histoire... Hummm... Laissez-moi examiner cela." WHERE entry = 2000000523;
UPDATE dbscript_string SET content_loc2 = "%s effleure les pages du livre." WHERE entry = 2000000524;
UPDATE dbscript_string SET content_loc2 = "Stupéfiant ! Vous avez mis la main dessus, $n !" WHERE entry = 2000000525;
UPDATE dbscript_string SET content_loc2 = "Superbe, Maltrake. On dirait bien que mes plans ont fonctionné à la perfection, n\'est-ce pas, mortel ?" WHERE entry = 2000000526;
UPDATE dbscript_string SET content_loc2 = "%s lâche un rire reptilien... Enfin, vous pensez que c\'est un rire." WHERE entry = 2000000527;
UPDATE dbscript_string SET content_loc2 = "Vous n\'auriez pas aidé la légion de Rochenoire si nous vous avions juste demandé de l\'aide, pas vrai ?" WHERE entry = 2000000528;
UPDATE dbscript_string SET content_loc2 = "Qu\'allez-vous faire d\'eux, maître ? Ils ne sont plus d\'aucune utilité, maintenant." WHERE entry = 2000000529;
UPDATE dbscript_string SET content_loc2 = "SILENCE, IMBÉCILE !" WHERE entry = 2000000530;
UPDATE dbscript_string SET content_loc2 = "Ils vivront, et ils verront les fruits de leur travail : la destruction et le chaos qui vont probablement suivre l\'invasion de la Gorge par la Légion de Rochenoire." WHERE entry = 2000000531;
UPDATE dbscript_string SET content_loc2 = "Je vous laisse maintenant, mortels. Vivants et avec ces babioles. Maltrake ! Montrez-leur les babioles !" WHERE entry = 2000000532;
UPDATE dbscript_string SET content_loc2 = "Tout de suite, maître !" WHERE entry = 2000000533;
UPDATE dbscript_string SET content_loc2 = "%s bat plus rapidement l\'air de ses puissantes ailes. Il se prépare à l\'envol." WHERE entry = 2000000534;
UPDATE dbscript_string SET content_loc2 = "Oh oui, l\'écaille, ne la perdez pas. Certains de mes frères de race dans les steppes Ardentes font des objets que seule l\'écaille des dragons noirs peut fortifier." WHERE entry = 2000000535;
UPDATE dbscript_string SET content_loc2 = "La légion de Rochenoire arrive, bande de nabots ! Nous allons réduire la terre en cendres et consumer les cieux ! Nul ne survivra..." WHERE entry = 2000000536;
UPDATE dbscript_string SET content_loc2 = "Nous devons nous hâter !" WHERE entry = 2000000537;
UPDATE dbscript_string SET content_loc2 = "Esprits de la Forêt, montrez-vous et obéissez à l\’appel de la Nature !" WHERE entry = 2000000538;
UPDATE dbscript_string SET content_loc2 = "Esprits de la forêt, nous avons besoin de vous ! Venez vite au Val calciné !" WHERE entry = 2000000539;
UPDATE dbscript_string SET content_loc2 = "Bleu plus rouge... Je prédis..." WHERE entry = 2000000541;
UPDATE dbscript_string SET content_loc2 = "$n, c\'est ça ! J\'ai enfin compris !" WHERE entry = 2000000542;
UPDATE dbscript_string SET content_loc2 = "Ah, la liberté ! Si brève, mais si douce..." WHERE entry = 2000007708;
UPDATE dbscript_string SET content_loc2 = "Bon, le devoir nous appelle, pas vrai ? En premier lieu, la cloche... pour vous rendre vigoureux !" WHERE entry = 2000007709;
UPDATE dbscript_string SET content_loc2 = "Ensuite, je placerai la roue... pour vous protéger du mal." WHERE entry = 2000007710;
UPDATE dbscript_string SET content_loc2 = "Et enfin la chandelle... pour brûler ceux qui voudraient vous faire échouer !" WHERE entry = 2000007711;
UPDATE dbscript_string SET content_loc2 = "Je ne sais pas, Jill. Si Klaven nous voyait fainéanter, nous pourrions finir comme l\'une de ces choses !" WHERE entry = 2000000794;
UPDATE dbscript_string SET content_loc2 = "Hé, Raven..." WHERE entry = 2000000791;
UPDATE dbscript_string SET content_loc2 = "Ouais ?" WHERE entry = 2000000792;
UPDATE dbscript_string SET content_loc2 = "Pouvons-nous faire une pause ? Mes pieds me font horriblement souffrir et ces... choses me donnent la chair de poule." WHERE entry = 2000000793;
UPDATE dbscript_string SET content_loc2 = "%s compulse les livres." WHERE entry = 2000000789;
UPDATE dbscript_string SET content_loc2 = "Bon, où ai-je laissé les formules de la KapitalRisk ?" WHERE entry = 2000000790;
UPDATE dbscript_string SET content_loc2 = "Et si l\'un meurt, deux se relèveront !" WHERE entry = 2000000788;
UPDATE dbscript_string SET content_loc2 = "Quelle douce ironie : les troupes de Hurlevent abandonnent les défenses de leur ville et sont utilisées comme forces de destruction par la Confrérie." WHERE entry = 2000000787;
UPDATE dbscript_string SET content_loc2 = "Fascinant." WHERE entry = 2000005423;
UPDATE dbscript_string SET content_loc2 = "Si jamais tu recommences ça, je te tords le cou." WHERE entry = 2000005424;
UPDATE dbscript_string SET content_loc2 = "%s tourne frénétiquement les pages d\'un tome épais." WHERE entry = 2000000786;
UPDATE dbscript_string SET content_loc2 = "Intéressant... Je vais essayer quelque chose." WHERE entry = 2000005421;
UPDATE dbscript_string SET content_loc2 = "Cansis. Tu es... un poulet." WHERE entry = 2000005422;
UPDATE dbscript_string SET content_loc2 = "Grek essayer !" WHERE entry = 2000005418;
UPDATE dbscript_string SET content_loc2 = "Cette huile pas bien pour Grek ! Vous penser que Grek mauviette en robe ?" WHERE entry = 2000005419;
UPDATE dbscript_string SET content_loc2 = "Cansis, ne bouge pas. Je vais essayer d\'utiliser le cristal." WHERE entry = 2000005420;
UPDATE dbscript_string SET content_loc2 = "Mes frères et mes sœurs. Écoutez-moi. Nous devons parler de la survie de notre peuple. Nous avons difficilement réussi à survivre à une catastrophe, mais je crains que la prochaine ne nous détruise." WHERE entry = 2000000571;
UPDATE dbscript_string SET content_loc2 = "Ne voyez-vous pas ce qui se passe ? Ouvrez les yeux. Cela nous crève la vue. Quelle est cette nouvelle alliance ? Pourquoi avons-nous trahi nos anciens alliés, pour nous ranger aux côtés des ennemis de tous les êtres vivants ?" WHERE entry = 2000000572;
UPDATE dbscript_string SET content_loc2 = "Non, vous avez tort ! C\'est nous qui avons été trahis !" WHERE entry = 2000000573;
UPDATE dbscript_string SET content_loc2 = "Et depuis quand les nains sont nos amis ?" WHERE entry = 2000000574;
UPDATE dbscript_string SET content_loc2 = "Les humains nous ont abandonnés à l\’heure la plus grave ! Leur prince lui-même a conduit l\’attaque sur nos terres." WHERE entry = 2000000575;
UPDATE dbscript_string SET content_loc2 = "Idiot ! Les serviteurs du prince étaient les Réprouvés, ceux-là même auxquels nous sommes alliés aujourd\’hui, et maintenant le prince nous a trahi !" WHERE entry = 2000000576;
UPDATE dbscript_string SET content_loc2 = "Le général des forestiers de Lune-d\’Argent a été un allié loyal !" WHERE entry = 2000000577;
UPDATE dbscript_string SET content_loc2 = "Vous parlez d\’elle comme d\’une amie ? Vous êtes un imbécile. Cette « dame noire » n\’est pas Sylvanas." WHERE entry = 2000000578;
UPDATE dbscript_string SET content_loc2 = "Le sang parle. Nous ne pouvons faire confiance à personne, sauf à nos proches !" WHERE entry = 2000000579;
UPDATE dbscript_string SET content_loc2 = "Du sang ? Et ces orcs, ces alliés de circonstance ? Nous aideront-ils ?" WHERE entry = 2000000580;
UPDATE dbscript_string SET content_loc2 = "Nos terres sont marquées par un grand chancre qui les ronge un peu plus chaque jour ! Notre besoin de magie nous affaiblit, et nous conduira à notre perte, comme autrefois !" WHERE entry = 2000000581;
UPDATE dbscript_string SET content_loc2 = "Comment vivre sans magie ?" WHERE entry = 2000000582;
UPDATE dbscript_string SET content_loc2 = "Contemplez les merveilles de cette cité ! C\'est de la magie !" WHERE entry = 2000000583;
UPDATE dbscript_string SET content_loc2 = "Mais quel prix ? Vous avez tous été aveuglés. Nous avons tous été aveuglés." WHERE entry = 2000000584;
UPDATE dbscript_string SET content_loc2 = "Vous nous traitez de traîtres ?" WHERE entry = 2000000585;
UPDATE dbscript_string SET content_loc2 = "Ce sont les magistères les traîtres ! Ils ont scellé notre destin !" WHERE entry = 2000000586;
UPDATE dbscript_string SET content_loc2 = "Sornettes d\'idéaliste. Retournez à vos forêts !" WHERE entry = 2000000587;
UPDATE dbscript_string SET content_loc2 = "Vous voulez que nous vivions comme des sauvages ?" WHERE entry = 2000000588;
UPDATE dbscript_string SET content_loc2 = "Il n\'est pas question de sauvagerie, c\'est une question de conscience. Les excès de notre peuple vont nous détruire, nous et tout ce que nous avons bâti au prix de tant de luttes." WHERE entry = 2000000589;
UPDATE dbscript_string SET content_loc2 = "Après tout ce que nous avons traversé pour survivre, nous n\'allons pas provoquer notre extinction." WHERE entry = 2000000590;
UPDATE dbscript_string SET content_loc2 = "Nous sommes au bord de l\'anéantissement. Nous devons changer de direction avant qu\'il ne soit trop tard." WHERE entry = 2000000591;
UPDATE dbscript_string SET content_loc2 = "Si nous sommes unis, il n\’est plus possible de nous ignorer !" WHERE entry = 2000000592;
UPDATE dbscript_string SET content_loc2 = "Soutenez-nous ! Nous devons agir dès maintenant pour sauver notre peuple." WHERE entry = 2000000593;
UPDATE dbscript_string SET content_loc2 = "Lor\'themar doit nous entendre ! On ne nous fera pas taire !" WHERE entry = 2000000594;
UPDATE dbscript_string SET content_loc2 = "Cela devrait suffire. Si vous avez de nouveau besoin de mes services, rappelez-moi." WHERE entry = 2000000595;
UPDATE dbscript_string SET content_loc2 = "L\'Alliance payera très cher sa trahison à notre égard. Nous n\'oublierons jamais, nous ne pardonnerons jamais." WHERE entry = 2000000596;
UPDATE dbscript_string SET content_loc2 = "Comment pouvez-vous nier que notre vie est meilleure ? Comment pouvez-vous refuser nos progrès ?" WHERE entry = 2000000597;
UPDATE dbscript_string SET content_loc2 = "Lor\'themar a remodelé Lune-d\’Argent. Nous pouvons vraiment être fiers du résultat." WHERE entry = 2000000598;
UPDATE dbscript_string SET content_loc2 = "Nos nouveaux alliés nous aideront à retrouver notre gloire passée. Ces liens résisteront au temps, et nous ne serons plus jamais trahis !" WHERE entry = 2000000599;
UPDATE dbscript_string SET content_loc2 = "Quittez ces lieux. <La relique émet un arc de flammes chauffées à blanc. Un souvenir se fait jour : un nain Sombrefer solitaire, entouré par sept cadavres, à genoux devant un monolithe de flammes.>" WHERE entry = 2000000548;
UPDATE dbscript_string SET content_loc2 = "Aidez-nous ! <La relique tombe en poussière. La vision de huit nains sombrefers en plein rituel étrange emplit votre cerveau.>" WHERE entry = 2000000550;
UPDATE dbscript_string SET content_loc2 = "Envoyez !" WHERE entry = 2000010182;
UPDATE dbscript_string SET content_loc2 = "Vous mourrez ici. <Votre esprit est assailli de visions de chaos et de destruction.>" WHERE entry = 2000001424;
UPDATE dbscript_string SET content_loc2 = "Profanateur... Votre incursion sera punie. <Un symbole de flammes irradie de la relique avant qu\'elle ne se désagrège au sol.>" WHERE entry = 2000001428;
UPDATE dbscript_string SET content_loc2 = "Nous devons nous concentrer sur notre tâche. Lorsque le sanctum des arcanes fonctionnera à plein régime, Lune-d\’Argent sera bien forcée de nous envoyer de l\’aide. Les sanctum des arcanes en état de marche sont une rareté." WHERE entry = 2000010180;
UPDATE dbscript_string SET content_loc2 = "Merci ! Je ne sais pas ce que ces brutes m\’auraient fait." WHERE entry = 2000000502;
UPDATE dbscript_string SET content_loc2 = "Retournons dans le bosquet, $gmortel:mortelle;." WHERE entry = 2000000387;
UPDATE dbscript_string SET content_loc2 = "Adieu, mon ami... Adieu..." WHERE entry = 2000000386;
UPDATE dbscript_string SET content_loc2 = "Prenez garde ! Prenez garde ! Une menace rôde aux alentours ! Veilleurs, soyez sur vos gardes !" WHERE entry = 2000000244;
UPDATE dbscript_string SET content_loc2 = "%s rit." WHERE entry = 2000000889;
UPDATE dbscript_string SET content_loc2 = "%s lance un regard assassin à Velen." WHERE entry = 2000000887;
UPDATE dbscript_string SET content_loc2 = "La Lumière ?" WHERE entry = 2000000888;
UPDATE dbscript_string SET content_loc2 = "Puisse la Lumière avoir pitié de votre âme. Ramenez-le dans sa cellule." WHERE entry = 2000000886;
UPDATE dbscript_string SET content_loc2 = "%s crache sur l\'image de Velen." WHERE entry = 2000000885;
UPDATE dbscript_string SET content_loc2 = "Vous serez exécuté aux premières lueurs du jour. Avez-vous quelque chose à déclarer ?" WHERE entry = 2000000884;
UPDATE dbscript_string SET content_loc2 = "Matis, lieutenant elfe de sang des Eperviers du soleil, vous avez été déclaré coupable de génocide envers le peuple draeneï." WHERE entry = 2000000883;
UPDATE dbscript_string SET content_loc2 = "Tu as entendu le prophète. Dégage, rebut !" WHERE entry = 2000000882;
UPDATE dbscript_string SET content_loc2 = "Faites venir l\’elfe de sang." WHERE entry = 2000000881;
UPDATE dbscript_string SET content_loc2 = "Jusqu\'à la mort !" WHERE entry = 2000000879;
UPDATE dbscript_string SET content_loc2 = "Vous allez le regretter, vermine." WHERE entry = 2000000880;
UPDATE dbscript_string SET content_loc2 = "Vous ne me prendrez jamais vivant !" WHERE entry = 2000000878;
UPDATE dbscript_string SET content_loc2 = "C\’est TOI qui as tout faux ! Je m\’occuperai de vous deux dès que j\’aurai tué ce fouineur de chaman !" WHERE entry = 2000000877;
UPDATE dbscript_string SET content_loc2 = "Faux ! C\’est TOI qui vas mourir, Tel\'athion l\’Impur !" WHERE entry = 2000000876;
UPDATE dbscript_string SET content_loc2 = "Oh ! Je ne m\'attendais pas à cela ! Débrouillez-vous, le Mince !" WHERE entry = 2000000602;
UPDATE dbscript_string SET content_loc2 = "Des mortels, ici ? Qu\'est-ce que ça signifie, pitoyable Roué ?" WHERE entry = 2000000603;
UPDATE dbscript_string SET content_loc2 = "N\'ayez crainte, $r. Jouez simplement le jeu." WHERE entry = 2000000604;
UPDATE dbscript_string SET content_loc2 = "Une simple nuisance, je vous assure ! Dites au Maître que sa prisonnière ne s\'échappera pas tant qu\'Akama et ses ligemorts veilleront sur elle." WHERE entry = 2000000605;
UPDATE dbscript_string SET content_loc2 = "Vous feriez bien de ne pas jouer avec moi, Akama. Illidan m\'a ordonné très précisément de surveiller la Gardienne de près. Si je découvre que vous m\'avez caché quoi que ce soit, je vous écraserai de mes propres mains !" WHERE entry = 2000000606;
UPDATE dbscript_string SET content_loc2 = "Pardonnez mes méthodes un peu rudes, mais le Traître ne doit pas apprendre la vérité. Mon secret doit le rester à tout prix !" WHERE entry = 2000000607;
UPDATE dbscript_string SET content_loc2 = "Si nous avons vraiment le même vœu, Akama, alors libérez-moi ! Si Illidan doit mourir, ce sera par ma main !" WHERE entry = 2000000608;
UPDATE dbscript_string SET content_loc2 = "Quand le moment sera venu, Maiev. J\'ai passé des années à préparer ce plan, je ne peux pas risquer de tout gâcher en dévoilant ma main trop tôt." WHERE entry = 2000000609;
UPDATE dbscript_string SET content_loc2 = "Sois maudit, Akama ! Je ne suis pas un pion sur ton échiquier... ma volonté est mienne. Quand je déchaînerai ma colère sur Illidan, cela n\'aura rien à voir avec tes stupides manigances !" WHERE entry = 2000000610;
UPDATE dbscript_string SET content_loc2 = "Nous commencerons demain l\'entraînement de nos dragons les plus prometteurs, n\'oubliez pas de leur amener des jouets à ronger." WHERE entry = 2000005028;
UPDATE dbscript_string SET content_loc2 = "Conseil Ombres venir contre vous Cogneguerre ! Nous furieux contre vous !" WHERE entry = 2000000613;
UPDATE dbscript_string SET content_loc2 = "Groar ! Moi être magichien Conseil ombres ! Moi venir pour terri... tettritor... Moi venir pour terres Cognegerre !" WHERE entry = 2000000614;
UPDATE dbscript_string SET content_loc2 = "Moi massacrouiller autre ogre Cogneguerre ! Moi être Conseil Ombres ! Venir me battre !" WHERE entry = 2000000615;
UPDATE dbscript_string SET content_loc2 = "Toi taire, stupide ! Pourquoi toi crier ?" WHERE entry = 2000000616;
UPDATE dbscript_string SET content_loc2 = "Moi vouloir faire eux croire... Moi vouloir être vrai. Eux chûr croire être Conseil ombres maintenant !" WHERE entry = 2000000617;
UPDATE dbscript_string SET content_loc2 = "Toi très malin. Nous faire ça avant beaucoup autres venir." WHERE entry = 2000000618;
UPDATE dbscript_string SET content_loc2 = "Ton bruit orc mourir bien. Mon bruit être meilleur." WHERE entry = 2000000619;
UPDATE dbscript_string SET content_loc2 = "Toi bien faire bruit. Bruit comme orc mourir." WHERE entry = 2000000620;
UPDATE dbscript_string SET content_loc2 = "Mon bruit orc mourir être meilleur de tous !" WHERE entry = 2000000621;
UPDATE dbscript_string SET content_loc2 = "Ça être chûr, ça être bien." WHERE entry = 2000000622;
UPDATE dbscript_string SET content_loc2 = "Mois dernier, moi gagner grand champi... champino... Moi gagner grand concours bruit orc mourir." WHERE entry = 2000000623;
UPDATE dbscript_string SET content_loc2 = "Eux tous être là ! Eux Cogneguerre être grosse colère ! Hourra !" WHERE entry = 2000000624;
UPDATE dbscript_string SET content_loc2 = "Venez, $n. Allons voir si ce plan peut fonctionner. En route, Copain !" WHERE entry = 2000000625;
UPDATE dbscript_string SET content_loc2 = "Bien, voyons comment utiliser les champignons que vous avez rapportés pour faire pousser de quoi nourrir les seigneurs-tourbe." WHERE entry = 2000000626;
UPDATE dbscript_string SET content_loc2 = "Attendons et voyons ce qui se passe !" WHERE entry = 2000000627;
UPDATE dbscript_string SET content_loc2 = "Copain, espèce de gros bébé ! Regardez, ça marche. Ça marche vraiment !! Il ne me reste plus qu\’à faire pousser assez de ces champignons ici et les seigneurs-tourbe devraient laisser les sporelins tranquilles !" WHERE entry = 2000000628;
UPDATE dbscript_string SET content_loc2 = "Oh, merci beaucoup, $n ! Grâce à vous, nous avons pu sauver à la fois les sporelins des seigneurs-tourbe, et les seigneurs-tourbe de notre courroux." WHERE entry = 2000000629;
UPDATE dbscript_string SET content_loc2 = "%s prend un morceau de l\’arbre champignon et le place devant son \"nez\". Apparemment satisfait, il le mange !" WHERE entry = 2000000630;
UPDATE dbscript_string SET content_loc2 = "%s déracine le reste de l\’arbre champignon et l\’emporte." WHERE entry = 2000000631;
UPDATE dbscript_string SET content_loc2 = "Lorsque le produit neutralisant se mélange à l\'eau du lac, l\'eau irradiée par le cristal semble s\'épurer." WHERE entry = 2000000633;
UPDATE dbscript_string SET content_loc2 = "Tedon, allez-y, essayez le produit neutralisant sur la créature mutante." WHERE entry = 2000000634;
UPDATE dbscript_string SET content_loc2 = "On dirait qu\'il est temps d\'ouvrir cette cage." WHERE entry = 2000000635;
UPDATE dbscript_string SET content_loc2 = "Alors, on applique le produit neutralisant..." WHERE entry = 2000000636;
UPDATE dbscript_string SET content_loc2 = "Ça marche !" WHERE entry = 2000000637;
UPDATE dbscript_string SET content_loc2 = "Par les sept cristaux d\'Ata\'mal ! D\'autres survivants ! Zhanaa, c\'est toi ? Comme je suis heureux de te revoir !" WHERE entry = 2000000638;
UPDATE dbscript_string SET content_loc2 = "Et qui avons-nous là ? Certaiment ce... crzzzk...du cette communication possible. Je vous salue dep... psshzzzk... Guet d\'azur." WHERE entry = 2000000639;
UPDATE dbscript_string SET content_loc2 = "Grâce à l\’analyse de votre signal, nous vous situons... actement au sud-est de la grande île sur laquelle nous nous sommes écrasés. Traversez la Cristalline pour... kshhhhkzzt. Désolé, il y a beaucoup de paras..." WHERE entry = 2000000640;
UPDATE dbscript_string SET content_loc2 = "Grandes nouvelles ! Vos efforts nous ont bel et bien sauvés, $n !" WHERE entry = 2000000641;
UPDATE dbscript_string SET content_loc2 = "Et maintenant appliquons la pommade sur ses blessures." WHERE entry = 2000000642;
UPDATE dbscript_string SET content_loc2 = "On dirait bien que cette pommade est inefficace. Quelle misère..." WHERE entry = 2000000643;
UPDATE dbscript_string SET content_loc2 = "Il reste peut-être encore une solution. Venez par ici, $n. Je vais vous expliquer ce dont j\’ai besoin." WHERE entry = 2000000644;
UPDATE dbscript_string SET content_loc2 = "Cet édifice est en infraction d\’au moins 8 ordonnances différentes !" WHERE entry = 2000000647;
UPDATE dbscript_string SET content_loc2 = "Je refuse d\’être tenu pour responsable de la catastrophe qui ne manquera pas d\’arriver à cause de l\’entretien hasardeux de ce bâtiment." WHERE entry = 2000000648;
UPDATE dbscript_string SET content_loc2 = "Hum, c\’est déjà beaucoup mieux. Allons jeter un œil à l\’intérieur." WHERE entry = 2000000649;
UPDATE dbscript_string SET content_loc2 = "Par les vilains tétons du torse pourri de Kil\’Jaeden, tout l\’arrière est explosé ! Comment voulez-vous que quelqu\’un puisse vivre ici ? J\’en ai assez vu !" WHERE entry = 2000000650;
UPDATE dbscript_string SET content_loc2 = "Dyvuun, c\'est bien vous ?" WHERE entry = 2000000651;
UPDATE dbscript_string SET content_loc2 = "Oui, Zhanaa, c\'est moi ! Votre signal est faible." WHERE entry = 2000000652;
UPDATE dbscript_string SET content_loc2 = "Je vous entends à peine... <parasites>...gnal !" WHERE entry = 2000000653;
UPDATE dbscript_string SET content_loc2 = "Zhanaa ! Ah, il faut vraiment que je répare ce truc !" WHERE entry = 2000000654;
UPDATE dbscript_string SET content_loc2 = "Cela devrait aller..." WHERE entry = 2000000655;
UPDATE dbscript_string SET content_loc2 = "Réveillez-vous, petit être. Sortez de votre léthargie. Vous êtes en sécurité, à présent." WHERE entry = 2000000656;
UPDATE dbscript_string SET content_loc2 = "%s grogne." WHERE entry = 2000000657;
UPDATE dbscript_string SET content_loc2 = "%s s\'assied." WHERE entry = 2000000658;
UPDATE dbscript_string SET content_loc2 = "Où suis-je ? Quel est cet endroit ?" WHERE entry = 2000000659;
UPDATE dbscript_string SET content_loc2 = "PAR LA LUMIÈRE D\’ELUNE ! Des érédars ! Démons maudits, que m\’avez-vous fait ? Je ne suis pas venue seule. Nous vous combattrons, comme nous vous avons combattus par le passé !" WHERE entry = 2000000660;
UPDATE dbscript_string SET content_loc2 = "%s retombe dans le coma." WHERE entry = 2000000661;
UPDATE dbscript_string SET content_loc2 = "%s se tourne vers Tuluun avec un rictus méprisant." WHERE entry = 2000000662;
UPDATE dbscript_string SET content_loc2 = "Comment puis-je espérer accomplir quoi que ce soit avec cette chose qui répand son hérésie ?" WHERE entry = 2000000663;
UPDATE dbscript_string SET content_loc2 = "%s pousse un soupir de dégoût en retournant à son travail." WHERE entry = 2000000664;
UPDATE dbscript_string SET content_loc2 = "Nous n\’avons pas besoin de ceux de votre espèce ici." WHERE entry = 2000000665;
UPDATE dbscript_string SET content_loc2 = "%s vous exhorte à le suivre." WHERE entry = 2000000666;
UPDATE dbscript_string SET content_loc2 = "%s désigne le totem de Coo." WHERE entry = 2000000667;
UPDATE dbscript_string SET content_loc2 = "%s vous fait signe de le suivre." WHERE entry = 2000000668;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Ritk kin\'chikx azul azur sommet..." WHERE entry = 2000000669;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Coo xip vol... Sauter ilos rivière. Trouver Tikti." WHERE entry = 2000000670;
UPDATE dbscript_string SET content_loc2 = "%s montre l\'autre rive du fleuve." WHERE entry = 2000000671;
UPDATE dbscript_string SET content_loc2 = "%s vous demande de le suivre." WHERE entry = 2000000672;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Loin vous dixik tak venir. Observer le fleuve kitch\'kal. Suivre. Ilog à Yor." WHERE entry = 2000000673;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Aller... Chercher yitix\'kil fond rivière. Sud !" WHERE entry = 2000000674;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Venez, $n. Sortons ensemble et purifiés de l\’eau." WHERE entry = 2000000675;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Nous partons maintenant, ensemble. À la recherche de Vark." WHERE entry = 2000000676;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Suivez-moi vers Vark." WHERE entry = 2000000677;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Vite, $n. Vark vous attend." WHERE entry = 2000000678;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Soyez témoin des sauvages exactions de nos adversaires, $n. Ils volent nos jeunes... nos faibles... Emprisonnés ici, ils sont à la merci des stratagèmes des Bras-hirsutes. Libérez-les, $gPromis:Promise; !" WHERE entry = 2000000679;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Merci, $n !" WHERE entry = 2000000680;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] La prophétie est vraie !" WHERE entry = 2000000681;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Les furbolgs Calmepins n\'oublieront pas votre bravoure de sitôt !" WHERE entry = 2000000682;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Ceux qui vivent encore au repaire des Calmepins vous accueilleront en héros !" WHERE entry = 2000000700;
UPDATE dbscript_string SET content_loc2 = "Hé, Cuistot ! Qu\'est-ce qu\'on mange ce soir ? Et ne me parle plus de poulet !" WHERE entry = 2000000683;
UPDATE dbscript_string SET content_loc2 = "Hum, Amiral. Laissez-moi gamberger une seconde... Heu... J\'finis la mise au point." WHERE entry = 2000000684;
UPDATE dbscript_string SET content_loc2 = "On dirait bien qu\'ce s\'rait d\'succulentes côtes de porc arrosées d\'leur propre délicieux jus d\'viande, et pis avec d\'la sauce secrète du cap\'taine Sanders - pour rel\'ver un peu l\'tout." WHERE entry = 2000000685;
UPDATE dbscript_string SET content_loc2 = "Et en dessert quequ\'religieuses, les pâtisseries j\'veux dire, fraîchement arrivées de Hurlevent sur les ailes des anges..." WHERE entry = 2000000686;
UPDATE dbscript_string SET content_loc2 = "Génial ! Vraiment ?" WHERE entry = 2000000687;
UPDATE dbscript_string SET content_loc2 = "Non. Pas vraiment. En fait..." WHERE entry = 2000000688;
UPDATE dbscript_string SET content_loc2 = "C\'est du poulet..." WHERE entry = 2000000689;
UPDATE dbscript_string SET content_loc2 = "Je te déteste, Cuistot." WHERE entry = 2000000690;
UPDATE dbscript_string SET content_loc2 = "Adamant, auriez-vous l\'amabilité de jeter un œil sur cette tablette et d\'en déchiffrer les runes ?" WHERE entry = 2000000691;
UPDATE dbscript_string SET content_loc2 = "Mais naturellement, tout ce que vous voudrez, chérie !" WHERE entry = 2000000692;
UPDATE dbscript_string SET content_loc2 = "Très bien, laissez-moi jeter un œil sur cette tablette." WHERE entry = 2000000693;
UPDATE dbscript_string SET content_loc2 = "%s lit lentement la tablette, en articulant chaque mot dans leur langue d\'origine." WHERE entry = 2000000694;
UPDATE dbscript_string SET content_loc2 = "Un chef de guerre naga ! Diantre..." WHERE entry = 2000000695;
UPDATE dbscript_string SET content_loc2 = "Eh bien, vous n\'allez pas aimer ce que j\'ai à vous dire, jeune demoiselle. Cette tablette est un courrier naga. Leur dirigeant, le chef de guerre Sriss\'tiz, a donné l\'ordre à ses troupes de prendre le reste de l\'île par la force." WHERE entry = 2000000696;
UPDATE dbscript_string SET content_loc2 = "Et on dirait qu\'il a des renforts qui arrivent de Nazjatar !" WHERE entry = 2000000697;
UPDATE dbscript_string SET content_loc2 = "Nous devons intervenir !" WHERE entry = 2000000698;
UPDATE dbscript_string SET content_loc2 = "Merci, Adamant. Je crois connaître la personne idéale pour ce travail." WHERE entry = 2000000699;
UPDATE dbscript_string SET content_loc2 = "Succube" WHERE entry = 2000000701;
UPDATE dbscript_string SET content_loc2 = "Espèce : Sayaad" WHERE entry = 2000000702;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000703;
UPDATE dbscript_string SET content_loc2 = "Fonction : Agents de renseignement, tortionnaires" WHERE entry = 2000000704;
UPDATE dbscript_string SET content_loc2 = "Les cruelles tentatrices sayaadi illustrent un côté plus subtil de la croisade nihiliste de la Légion." WHERE entry = 2000000705;
UPDATE dbscript_string SET content_loc2 = "Elles se délectent de l\’angoisse et de la souffrance qu\’elles infligent... Leurs interrogatoires meurtriers sont la source de bien des cauchemars." WHERE entry = 2000000706;
UPDATE dbscript_string SET content_loc2 = "Shivarra" WHERE entry = 2000000707;
UPDATE dbscript_string SET content_loc2 = "Espèce : Shivarra" WHERE entry = 2000000708;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000709;
UPDATE dbscript_string SET content_loc2 = "Fonction : Prêtresses, chapelains militaires" WHERE entry = 2000000710;
UPDATE dbscript_string SET content_loc2 = "Ferventes et douées d\'un charisme démesuré, ces prêtresses ne vivent que pour prêcher l\'empire des terribles visions de Sargeras." WHERE entry = 2000000711;
UPDATE dbscript_string SET content_loc2 = "Elles sont la force motrice de la Croisade ardente." WHERE entry = 2000000712;
UPDATE dbscript_string SET content_loc2 = "Seigneur de l\'effroi" WHERE entry = 2000000713;
UPDATE dbscript_string SET content_loc2 = "Espèce : Nathrezim" WHERE entry = 2000000714;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000715;
UPDATE dbscript_string SET content_loc2 = "Fonction : Police secrète, interrogateur" WHERE entry = 2000000716;
UPDATE dbscript_string SET content_loc2 = "Les nathrezims sont d\'impitoyables scélérats qui se nourrissent de l\'énergie des créatures mortelles." WHERE entry = 2000000717;
UPDATE dbscript_string SET content_loc2 = "Ils usent de terreur et d\'artifices, montant les frères les uns contre les autres, et des mondes entiers tombent sous leur ténébreuse influence." WHERE entry = 2000000718;
UPDATE dbscript_string SET content_loc2 = "Érédar" WHERE entry = 2000000719;
UPDATE dbscript_string SET content_loc2 = "Espèce : Érédar" WHERE entry = 2000000720;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000721;
UPDATE dbscript_string SET content_loc2 = "Fonction : Suzerain, stratège" WHERE entry = 2000000722;
UPDATE dbscript_string SET content_loc2 = "Les démonistes érédars, puissants et machiavéliques, étaient jadis de nobles membres de notre peuple." WHERE entry = 2000000723;
UPDATE dbscript_string SET content_loc2 = "Mais ils ont succombé à l\'attrait de la malfaisance de Sargeras. À présent ils sont devenus nos ennemis les plus acharnés." WHERE entry = 2000000724;
UPDATE dbscript_string SET content_loc2 = "Seigneur des abîmes" WHERE entry = 2000000725;
UPDATE dbscript_string SET content_loc2 = "Espèce : Annihilan" WHERE entry = 2000000726;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000727;
UPDATE dbscript_string SET content_loc2 = "Fonction : Généraux, commandants des forces terrestres" WHERE entry = 2000000728;
UPDATE dbscript_string SET content_loc2 = "Ces destructeurs colossaux font office de chefs militaires de la Légion." WHERE entry = 2000000729;
UPDATE dbscript_string SET content_loc2 = "Ils sont aussi cruels que puissants, et savourent la destruction de tout ce qui existe." WHERE entry = 2000000730;
UPDATE dbscript_string SET content_loc2 = "Gan\'arg" WHERE entry = 2000000731;
UPDATE dbscript_string SET content_loc2 = "Espèce : Mo\'arg" WHERE entry = 2000000732;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000733;
UPDATE dbscript_string SET content_loc2 = "Fonction : Bricoleurs, constructeurs de machines" WHERE entry = 2000000734;
UPDATE dbscript_string SET content_loc2 = "Ces mo\'arg chétifs sont très habiles et servent souvent d\'assistants à leurs semblables plus imposants." WHERE entry = 2000000735;
UPDATE dbscript_string SET content_loc2 = "D\'une nature malfaisante, ils aiment utiliser leur propres machines construites à la hâte." WHERE entry = 2000000736;
UPDATE dbscript_string SET content_loc2 = "Mo\'arg" WHERE entry = 2000000737;
UPDATE dbscript_string SET content_loc2 = "Espèce : Mo\'arg" WHERE entry = 2000000738;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000739;
UPDATE dbscript_string SET content_loc2 = "Fonction : Ingénieurs, scientifiques" WHERE entry = 2000000740;
UPDATE dbscript_string SET content_loc2 = "Bien qu\’elles ressemblent à de grosses brutes, ces horreurs couturées de fer sont des ingénieurs et des forgerons particulièrement brillants." WHERE entry = 2000000741;
UPDATE dbscript_string SET content_loc2 = "Leurs assemblages de fer noir ont semé la mort sur un millier de mondes." WHERE entry = 2000000742;
UPDATE dbscript_string SET content_loc2 = "Gangregarde" WHERE entry = 2000000743;
UPDATE dbscript_string SET content_loc2 = "Espèce : Mo\'arg" WHERE entry = 2000000744;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000745;
UPDATE dbscript_string SET content_loc2 = "Fonction : Soldats réguliers" WHERE entry = 2000000746;
UPDATE dbscript_string SET content_loc2 = "Enrôlés parmi le peuple mo\'arg, ces guerriers font office de soldats du rang de la Légion." WHERE entry = 2000000747;
UPDATE dbscript_string SET content_loc2 = "Ils sont intrépides et d\'une loyauté inébranlable envers la volonté divine de leur maître." WHERE entry = 2000000748;
UPDATE dbscript_string SET content_loc2 = "Garde funeste" WHERE entry = 2000000749;
UPDATE dbscript_string SET content_loc2 = "Espèce : Ered\'ruine" WHERE entry = 2000000750;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000751;
UPDATE dbscript_string SET content_loc2 = "Fonction : Troupes d\'assaut, massacreurs" WHERE entry = 2000000752;
UPDATE dbscript_string SET content_loc2 = "Puissantes et déterminées, ces aberrations ailées sont connues dans tout l\'univers pour leur brutalité." WHERE entry = 2000000753;
UPDATE dbscript_string SET content_loc2 = "Les gardes funestes sont éperdument dévoués à leurs maîtres, les seigneurs des abîmes, et se repaissent de la destruction des mondes." WHERE entry = 2000000754;
UPDATE dbscript_string SET content_loc2 = "Garde-courroux" WHERE entry = 2000000755;
UPDATE dbscript_string SET content_loc2 = "Espèce : Érédar" WHERE entry = 2000000756;
UPDATE dbscript_string SET content_loc2 = "Affiliation : Légion ardente" WHERE entry = 2000000757;
UPDATE dbscript_string SET content_loc2 = "Fonction : Garde d\'honneur" WHERE entry = 2000000758;
UPDATE dbscript_string SET content_loc2 = "Rusés et minutieux, les garde-courroux assurent souvent la protection des maîtres érédars de la Légion." WHERE entry = 2000000759;
UPDATE dbscript_string SET content_loc2 = "Leur efficacité en combat ne doit jamais être sous-estimée." WHERE entry = 2000000760;
UPDATE dbscript_string SET content_loc2 = "Et les esprits de la terre ?" WHERE entry = 2000000761;
UPDATE dbscript_string SET content_loc2 = "%s a un petit rire." WHERE entry = 2000000762;
UPDATE dbscript_string SET content_loc2 = "Les esprits de la terre sont bien présents dans cette pièce, mon enfant. Après tout, nous sommes sous terre, n\'est-ce pas ?" WHERE entry = 2000000763;
UPDATE dbscript_string SET content_loc2 = "Ils sont stoïques, ces esprits de la terre. C\'est par leur entremise que les chamans apprennent à endurer les vicissitudes de l\'existence." WHERE entry = 2000000764;
UPDATE dbscript_string SET content_loc2 = "Les rochers sont là lorsque nous entrons dans ce monde, et ils y seront encore longtemps après notre départ." WHERE entry = 2000000765;
UPDATE dbscript_string SET content_loc2 = "Debout... nous avons d\'autres choses à discuter." WHERE entry = 2000000766;
UPDATE dbscript_string SET content_loc2 = "Arrêtez-vous un instant, mon enfant, et fermez les yeux. Sentez-vous les courants qui parcourent les airs ?" WHERE entry = 2000000767;
UPDATE dbscript_string SET content_loc2 = "Les deux étudiants ferment les yeux et s\'inclinent." WHERE entry = 2000000768;
UPDATE dbscript_string SET content_loc2 = "Oui, maître." WHERE entry = 2000000769;
UPDATE dbscript_string SET content_loc2 = "Les esprits de l\’air se trouvent même ici, sous terre. Ils n\’aiment pas être entourés, cernés par la terre... leur précieuse liberté est limitée dans un tel endroit." WHERE entry = 2000000770;
UPDATE dbscript_string SET content_loc2 = "Ils sont capricieux et facile à distraire, ces esprits... mais ils partagent leurs secrets avec ceux qui savent les écouter." WHERE entry = 2000000771;
UPDATE dbscript_string SET content_loc2 = "%s sourit discrètement en regardant ses élèves faire de gros efforts pour se concentrer." WHERE entry = 2000000772;
UPDATE dbscript_string SET content_loc2 = "Venez. Les secrets du vent ne sont pas la seule chose que nous ayons à étudier." WHERE entry = 2000000773;
UPDATE dbscript_string SET content_loc2 = "Sentez-vous la chaleur, mes enfants ?" WHERE entry = 2000000774;
UPDATE dbscript_string SET content_loc2 = "Fascinés par leur professeur, les étudiants acquiescent lentement." WHERE entry = 2000000775;
UPDATE dbscript_string SET content_loc2 = "Concentrez-vous sur cette chaleur..." WHERE entry = 2000000776;
UPDATE dbscript_string SET content_loc2 = "La force du feu vient du changement. Cette force est considérable, mais toujours instable et imprévisible." WHERE entry = 2000000777;
UPDATE dbscript_string SET content_loc2 = "Cela s\'applique également aux esprits eux-mêmes. Ils sont souvent prompts à s\'emporter. Il vous faudra apprendre à les écouter sans devenir la proie de leurs humeurs." WHERE entry = 2000000778;
UPDATE dbscript_string SET content_loc2 = "Cela suffit pour l\'instant. Venez." WHERE entry = 2000000779;
UPDATE dbscript_string SET content_loc2 = "Cette eau ne semble-t\'elle pas paisible ? Écoutez ce filet qui descend du plafond, cela pourra vous aider à distinguer sa véritable voix..." WHERE entry = 2000000780;
UPDATE dbscript_string SET content_loc2 = "Les esprits de l\'eau sont probablement les plus ouverts. Ils sont généreux et compréhensifs. Si vous éprouvez des difficultés pour entendre les voix, vous feriez bien de commencer par l\'eau." WHERE entry = 2000000781;
UPDATE dbscript_string SET content_loc2 = "Fermez les yeux, mes enfants, et écoutez l\'eau qui court dans ces salles." WHERE entry = 2000000782;
UPDATE dbscript_string SET content_loc2 = "Les deux étudiants ferment les yeux et font un gros effort pour entendre les voix." WHERE entry = 2000000783;
UPDATE dbscript_string SET content_loc2 = "%s hoche la tête pensivement, puis s\'éclaircit la gorge." WHERE entry = 2000000784;
UPDATE dbscript_string SET content_loc2 = "Debout, mes enfants... Cela suffit pour le moment." WHERE entry = 2000000785;
UPDATE dbscript_string SET content_loc2 = "Salutations, mortels ! Je viens d\'être activé par les Disques de Norgannon pour vous assister !" WHERE entry = 2000000823;
UPDATE dbscript_string SET content_loc2 = "Vos disques sont prêts ! Veuillez engager les Disques de Norgannon encore une fois, pour les récupérer !" WHERE entry = 2000000824;
UPDATE dbscript_string SET content_loc2 = "Des bruits de pas traînants résonnent dans toute la caverne." WHERE entry = 2000000600;
UPDATE dbscript_string SET content_loc2 = "Éloignez-vous de ces cristaux ! Ou mourez !" WHERE entry = 2000000601;
UPDATE dbscript_string SET content_loc2 = "%s lance un regard vers Hargne, comme s\'il avait des scrupules à parler." WHERE entry = 2000000006;
UPDATE dbscript_string SET content_loc2 = "Merci, chaman. Je suis né à nouveau, et je vais brûler plus intensément que jamais !" WHERE entry = 2000000007;
UPDATE dbscript_string SET content_loc2 = "Écumeurs calmepins, le temps de frapper est venu ! Chargez !" WHERE entry = 2000000008;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Qu\'est-ce que cela peut bien vouloir dire ?" WHERE entry = 2000000027;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Contemplez la prophétie !" WHERE entry = 2000000825;
UPDATE dbscript_string SET content_loc2 = "[Furbolg] Esprits nous accordent vision !" WHERE entry = 2000000826;
UPDATE dbscript_string SET content_loc2 = "Grek essayer !" WHERE entry = 2000000310;
UPDATE dbscript_string SET content_loc2 = "Cette huile pas bien pour Grek ! Vous penser que Grek mauviette en robe ?" WHERE entry = 2000000311;
UPDATE dbscript_string SET content_loc2 = "Merci de m\'avoir sauvée, $n ! Mon père va être fou de joie !" WHERE entry = 2000000313;
UPDATE dbscript_string SET content_loc2 = "Vous ne regardez jamais votre courrier ?" WHERE entry = 2000000827;
UPDATE dbscript_string SET content_loc2 = "Et comment suis-je censé remettre tous ces messages ?" WHERE entry = 2000000828;
UPDATE dbscript_string SET content_loc2 = "Il faut que je me fasse muter avant que ce boulot ne me fasse complètement craquer..." WHERE entry = 2000000829;
UPDATE dbscript_string SET content_loc2 = "Il s\’imagine qu\’il est important. Il n\’arrête pas de me bousculer... Je lui montrerai. Je leur montrerai à tous !" WHERE entry = 2000000830;
UPDATE dbscript_string SET content_loc2 = "Si seulement je pouvais retourner dans le fourré aux champignons. Je n\’avais jamais vu un aussi beau cercle de ma vie !" WHERE entry = 2000000831;
UPDATE dbscript_string SET content_loc2 = "Le combat va commencer ! Les impitoyables Jumeaux bourbesang contre $n !" WHERE entry = 2000000832;
UPDATE dbscript_string SET content_loc2 = "Inimaginable ! $n a battu les Jumeaux bourbesang !" WHERE entry = 2000000833;
UPDATE dbscript_string SET content_loc2 = "Les Grands Bleus règnent en maîtres !" WHERE entry = 2000000834;
UPDATE dbscript_string SET content_loc2 = "Tout droit venu des hautes cimes des Tranchantes, voici Rokdar le Seigneur scindé ! $n est $gparti:partie; pour disputer le combat de sa vie." WHERE entry = 2000000835;
UPDATE dbscript_string SET content_loc2 = "Une fois de plus, $n remporte la victoire !" WHERE entry = 2000000836;
UPDATE dbscript_string SET content_loc2 = "Ça tout berk ! Mogor faire bien mieux à la bagarre !" WHERE entry = 2000000837;
UPDATE dbscript_string SET content_loc2 = "D\’origine inconnue : Skra\'gath ! $n $gpourra-t-il:pourra-t-elle; survivre au déferlement des énergies du vide ?" WHERE entry = 2000000838;
UPDATE dbscript_string SET content_loc2 = "Mogor pas épaté ! Skra\'gath fait avec seulement air et ombres ! Tout mou comme les pitits norcs !" WHERE entry = 2000000839;
UPDATE dbscript_string SET content_loc2 = "Et voici le moment tant attendu ! Le champion Cogneguerre va faire sa première apparition depuis des semaines dans l\’Anneau de sang ! $n $gva-t-il:va-t-elle; s\’effondrer aussi facilement que les adversaires précédents du champion ? C\’est ce que nous allons voir..." WHERE entry = 2000000840;
UPDATE dbscript_string SET content_loc2 = "KOI !? PAS POSSIBLE !! Te battre avec Mogor maintenant ! Mogor tout casser !" WHERE entry = 2000000841;
UPDATE dbscript_string SET content_loc2 = "Mogor vous a défié. Vous devez accepter ! Entrez dans l\’arène si vous êtes $gprêt:prête; à vous battre." WHERE entry = 2000000842;
UPDATE dbscript_string SET content_loc2 = "Pour la première fois dans l\’histoire de l\’arène de sang, Mogor a choisi de faire valoir son droit au combat ! Sur ce sol dévasté, $n va affronter Mogor, héros des Cogneguerre !" WHERE entry = 2000000843;
UPDATE dbscript_string SET content_loc2 = "Maintenant ça être le vrai champion ! Je donne chance de filer à petit être. Filer tout de suite avant que Mogor écrouba... que Mogor dértui... Filer avant que Mogor TUE !" WHERE entry = 2000000844;
UPDATE dbscript_string SET content_loc2 = "Plus de chance ! Maintenant toi payer ogre !" WHERE entry = 2000000845;
UPDATE dbscript_string SET content_loc2 = "$n a battu Mogor, le héros des Cogneguerre ! Hourra pour $n !" WHERE entry = 2000000846;
UPDATE dbscript_string SET content_loc2 = "Imbéciles ! Mauviettes ! Tzerak n\’a pas besoin de votre aide !" WHERE entry = 2000000847;
UPDATE dbscript_string SET content_loc2 = "Ces pictographies semblent raconter une histoire. Cette silhouette au début ressemble à l\’une de ces créatures rencontrées au Guet d\’azur." WHERE entry = 2000000848;
UPDATE dbscript_string SET content_loc2 = "Non, ce n\’est pas possible... Il est expliqué que cette créature est devenue volontairement un serviteur de la Légion. Elle s\'est transformée en satyre, et a reçu la « bénédiction » de la Légion." WHERE entry = 2000000849;
UPDATE dbscript_string SET content_loc2 = "Nous devons vaincre ces satyres, $n. Nous ne souffrirons pas la présence de la Légion sur ces terres !" WHERE entry = 2000000850;
UPDATE dbscript_string SET content_loc2 = "Morae... c\'est toi ?" WHERE entry = 2000000277;
UPDATE dbscript_string SET content_loc2 = "Non, vous n\’êtes pas Morae, mais je sens que vous la connaissez. Lorsque vous la verrez, dites-lui que j\’ai survécu à l\’accident, mais que les infiltrateurs m\’ont achevé." WHERE entry = 2000000278;
UPDATE dbscript_string SET content_loc2 = "Qu\'elle sache... que ma dernière pensée a été pour elle..." WHERE entry = 2000000279;
UPDATE dbscript_string SET content_loc2 = "Je suis à votre service, seigneur." WHERE entry = 2000000851;
UPDATE dbscript_string SET content_loc2 = "Je veux que tous vos pisteurs se mettent à la recherche de ce criminel, Matis le Cruel. J\’ai donné des lance-fusées aux agents de confiance de la Main. Si vous ou l\’un de vos pisteurs voyez une fusée, c\’est qu\’un agent a trouvé Matis et a besoin d\’aide. Et n\’oubliez pas que je veux qu\’on me ramène Matis vivant." WHERE entry = 2000000852;
UPDATE dbscript_string SET content_loc2 = "Ce sera fait, seigneur." WHERE entry = 2000000853;
UPDATE dbscript_string SET content_loc2 = "%s appelle ses camarades." WHERE entry = 2000000854;
UPDATE dbscript_string SET content_loc2 = "Frères et sœurs, nous partons en chasse ce soir ! Un elfe de sang appelé Matis doit être ramené et jugé ! Le premier à le trouver aura droit à une semaine de vacances à bord de l\’Exodar !" WHERE entry = 2000000855;
UPDATE dbscript_string SET content_loc2 = "La magie d\'Avruu... Elle me contrôle toujours. Combattez-moi, $gmortel:mortelle;, c\'est le seul moyen de briser le sort !" WHERE entry = 2000005654;
UPDATE dbscript_string SET content_loc2 = "Poursuivons notre discussion dans un endroit plus... privé. Suivez-moi, $n." WHERE entry = 2000005786;
UPDATE dbscript_string SET content_loc2 = "Vous ne pensiez quand même pas que je ferais d\'autres affaires avec vous, si ?! Parfois, le meilleur moyen de redevenir bénéficiaire, c\'est de savoir quand limiter ses pertes." WHERE entry = 2000005787;
UPDATE dbscript_string SET content_loc2 = "%s commence à pétarader quand son moteur tombe en panne." WHERE entry = 2000005788;
UPDATE dbscript_string SET content_loc2 = "À présent, voyons..." WHERE entry = 2000000856;
UPDATE dbscript_string SET content_loc2 = "Cette traduction m\’en a appris davantage sur les exploits alcoolisés d\’un certain « Andrew Clementine » que sur l\’auteur du journal." WHERE entry = 2000000857;
UPDATE dbscript_string SET content_loc2 = "Mais quoi que l\’on puisse penser de la... hum... « contribution » de messire Clementine, il s\’agit apparemment du journal d\’un certain Nolkai. Les dernières entrées décrivent une violente bataille." WHERE entry = 2000000858;
UPDATE dbscript_string SET content_loc2 = "Jetez un coup d\'œil à ce passage..." WHERE entry = 2000000859;
UPDATE dbscript_string SET content_loc2 = "%s vous regarde d\’un air méfiant." WHERE entry = 2000010038;
UPDATE dbscript_string SET content_loc2 = "Eh bien... Vous pensiez pouvoir me duper, n\’est-ce pas ? Moi, le plus grand génie criminel qu\’Azeroth ait jamais connu ?" WHERE entry = 2000010039;
UPDATE dbscript_string SET content_loc2 = "Pas même un petit bonjour à votre vieil ami Narain ? Vous vous attendiez à trouver qui ?" WHERE entry = 2000010037;
UPDATE dbscript_string SET content_loc2 = "À vos marques, prêts, PARTEZ !" WHERE entry = 2000000867;
UPDATE dbscript_string SET content_loc2 = "HÉ ! Ce n\’est pas juste !" WHERE entry = 2000000868;
UPDATE dbscript_string SET content_loc2 = "J\’ai gagné !" WHERE entry = 2000000869;
UPDATE dbscript_string SET content_loc2 = "Oui, je comprends." WHERE entry = 2000000870;
UPDATE dbscript_string SET content_loc2 = "Tu veux faire une autre course ? Au meilleur sur trois courses !" WHERE entry = 2000000871;
UPDATE dbscript_string SET content_loc2 = "Désolé de quitter un client, mais nous ne pouvons rester plus longtemps, nous avons des gens à escroquer. Nous serons de retour plus tard dans la journée. Au revoir !" WHERE entry = 2000000872;
UPDATE dbscript_string SET content_loc2 = "Je cherche des gardes du corps pour protéger la caravane de Gizelton. Nous sommes sur la route à l\'est de la hutte de Kormek, au nord du village des centaures Kolkar." WHERE entry = 2000000873;
UPDATE dbscript_string SET content_loc2 = "La caravane de Gizelton doit se mettre en route ! Nous serons de retour bientôt, mais si vous ne pouvez pas attendre, allez à la hutte de Kormek vers le nord. Nous ouvrons boutique dans environ une heure." WHERE entry = 2000000874;
UPDATE dbscript_string SET content_loc2 = "Je suis Gréeur Gizelton. Je cherche de l\'assistance pour escorter ma caravane au-delà du convent de Mannoroc. Je suis sur la route à l\'est du village de Proie-de-l\'Ombre." WHERE entry = 2000000875;
UPDATE dbscript_string SET content_loc2 = "%s commence un sort d\'invocation..." WHERE entry = 2000005521;
UPDATE dbscript_string SET content_loc2 = "Cachez-vous ! Vite, avant qu\'ils ne vous voient ! Reculez !" WHERE entry = 2000005789;
UPDATE dbscript_string SET content_loc2 = "Halte !" WHERE entry = 2000005790;
UPDATE dbscript_string SET content_loc2 = "Qu\'y a-t-il, seigneur ?" WHERE entry = 2000005791;
UPDATE dbscript_string SET content_loc2 = "Zarath, il faut que vous retourniez au Temple noir sur-le-champ ! Il... Il semble que j\'aie égaré temporairement les ordres du seigneur Illidan. Faites vite !" WHERE entry = 2000005792;
UPDATE dbscript_string SET content_loc2 = "Seigneur, vous ne vous attendiez sûrement pas à ce que je vous laisse sans protection. Le seigneur Illidan aurait ma peau s\'il vous arrivait quoi que ce soit." WHERE entry = 2000005793;
UPDATE dbscript_string SET content_loc2 = "Zarath, je suis parfaitement capable de remonter la route jusqu\'à la halte de l\'Éclipse tout seul. Si nous n\'assurons pas la livraison de la missive, ce sont nos peaux à nous deux qu\'aura le seigneur Illidan ! Vous pouvez disposez !" WHERE entry = 2000005794;
UPDATE dbscript_string SET content_loc2 = "Comme vous le souhaitez, seigneur." WHERE entry = 2000005795;
UPDATE dbscript_string SET content_loc2 = "Ah, un joli petit fagot de chardons sanglants... Sans doute abandonné par l\'un des sales drogués de la halte de l\'Éclipse." WHERE entry = 2000005796;
UPDATE dbscript_string SET content_loc2 = "Leur malheur fait vraiment mon bonheur..." WHERE entry = 2000005797;
UPDATE dbscript_string SET content_loc2 = "L\'heure de frapper est venue. Supprimez Icarius." WHERE entry = 2000005798;
UPDATE dbscript_string SET content_loc2 = "%s place le fagot de chardons sanglants dans sa robe." WHERE entry = 2000005799;
UPDATE dbscript_string SET content_loc2 = "Je devrais te secouer la tête un bon coup ! J\’ai l\’impression que les lignes telluriques te brouillent le cerveau. Non, Darius, ce dont nous avons besoin, c\’est d\’une grande masse d\’eau riche en minéraux. Même de faibles signaux arcaniques pourront s\'accumuler dans le soufre et la chaux." WHERE entry = 2000000800;
UPDATE dbscript_string SET content_loc2 = "Dé... désolé, monsieur." WHERE entry = 2000000801;
UPDATE dbscript_string SET content_loc2 = "%s se lance dans une divination des Arcanes." WHERE entry = 2000000802;
UPDATE dbscript_string SET content_loc2 = "Maudit cristal de divination ! Je ne capte rien par ici." WHERE entry = 2000000803;
UPDATE dbscript_string SET content_loc2 = "M-m-monsieur... Vous avez essayé de bien secouer le cristal ? Il est possible que les lignes telluriques obscurcissent la lecture." WHERE entry = 2000000804;
UPDATE dbscript_string SET content_loc2 = "Ne sois pas aussi dur envers le gamin, Alturus. N\’oublie pas que c\’est Cedric lui-même qui te l\’a envoyé." WHERE entry = 2000000805;
UPDATE dbscript_string SET content_loc2 = "Il y a une odeur bizarre." WHERE entry = 2000001920;
UPDATE dbscript_string SET content_loc2 = "J\’ai un ver de terre écrabouillé sur mes chaussures." WHERE entry = 2000001919;
UPDATE dbscript_string SET content_loc2 = "Pouah... ce n\’est pas un poisson !" WHERE entry = 2000001918;
UPDATE dbscript_string SET content_loc2 = "Je crois que je vois quelque chose." WHERE entry = 2000001917;
UPDATE dbscript_string SET content_loc2 = "J\’ai cru entendre un bruit." WHERE entry = 2000001916;
UPDATE dbscript_string SET content_loc2 = "Ça me fait penser aux histoires de poisson que Billy raconte." WHERE entry = 2000001915;
UPDATE dbscript_string SET content_loc2 = "Tu t\'imagines ?" WHERE entry = 2000001914;
UPDATE dbscript_string SET content_loc2 = "Mon père dit que ce ne sont que des histoires." WHERE entry = 2000001913;
UPDATE dbscript_string SET content_loc2 = "C\’est super." WHERE entry = 2000001912;
UPDATE dbscript_string SET content_loc2 = "Qui ose souiller l\'autel de Gorgrom le Mange-dragon ?" WHERE entry = 2000000327;
UPDATE dbscript_string SET content_loc2 = "Je vais l\'écraser et l\'écorcher et le manger, et lui briser les os un par un quand j\'en aurai fini avec lui." WHERE entry = 2000000328;
UPDATE dbscript_string SET content_loc2 = "Misha laisse échapper un puissant rugissement en griffant l\'autel du gronn." WHERE entry = 2000000329;
UPDATE dbscript_string SET content_loc2 = "Tout est prêt, porteguerre." WHERE entry = 2000005432;
UPDATE dbscript_string SET content_loc2 = "Le seigneur funeste Kazzak sera content. Il faut augmenter la cadence de vos attaques. Détruisez les bastions orcs et nains au plus vite." WHERE entry = 2000005433;
UPDATE dbscript_string SET content_loc2 = "Porteguerre, cela impliquera de faire donner tous les infernaux du bastion. Cela pourrait nous rendre vulnérables à une contre-attaque." WHERE entry = 2000005434;
UPDATE dbscript_string SET content_loc2 = "Ne vous inquiétez pas. J\'ai augmenté le rythme de production à la Forgemort. Vous aurez tous les infernaux qu\'il vous faudra pour exécuter vos ordres. N\'échouez pas, Jovaan." WHERE entry = 2000005435;
UPDATE dbscript_string SET content_loc2 = "Il sera fait selon vos ordres, porteguerre. Une dernière question, si je puis..." WHERE entry = 2000005436;
UPDATE dbscript_string SET content_loc2 = "Oui ?" WHERE entry = 2000005437;
UPDATE dbscript_string SET content_loc2 = "Y a quoi dans la caisse ?" WHERE entry = 2000005438;
UPDATE dbscript_string SET content_loc2 = "La caisse ? Je n\'ai pas envoyé de caisse, Jovaan. N\'avez-vous rien de plus important à faire ? Allez vous en occuper !" WHERE entry = 2000005439;
UPDATE dbscript_string SET content_loc2 = "Vous m\'avez sauvé, créature de chair ! Cette noble action sera rapportée à Ameer." WHERE entry = 2000000227;
UPDATE dbscript_string SET content_loc2 = "Rares sont les gens dans le monde qui savent préparer le Gardien de l\’éveil, $n. Nous avons de la chance que je sois l\’un d\’entre eux." WHERE entry = 2000005665;
UPDATE dbscript_string SET content_loc2 = "%s s\’agenouille, remplissant la fiole avec l\’eau du puits de lune." WHERE entry = 2000005666;
UPDATE dbscript_string SET content_loc2 = "%s mélange les herbes avec l\’eau du puits de lune pour créer le Gardien de l\’éveil." WHERE entry = 2000005667;
UPDATE dbscript_string SET content_loc2 = "La potion est terminée. J\’espère seulement que Clintar nous pardonnera d\’interrompre son voyage dans le Rêve d\’émeraude." WHERE entry = 2000005668;
UPDATE dbscript_string SET content_loc2 = "%s se dresse brusquement sur les genoux, mais semble être ailleurs." WHERE entry = 2000005669;
UPDATE dbscript_string SET content_loc2 = "%s se lève et commence à reconnaître ce qui l\'entoure." WHERE entry = 2000005670;
UPDATE dbscript_string SET content_loc2 = "Approche, $gmon jeune ami:ma jeune amie;. Tu n\'as rien à craindre de la part |2 $n ou de la mienne." WHERE entry = 2000005671;
UPDATE dbscript_string SET content_loc2 = "%s s\'approche prudemment de la gardienne Elaira." WHERE entry = 2000005672;
UPDATE dbscript_string SET content_loc2 = "Je vais t\'apprendre comment aider $n à remplir une tâche très importante. Je te promets qu\'après cela tu seras libre de rentrer chez toi." WHERE entry = 2000005673;
UPDATE dbscript_string SET content_loc2 = "Tu entendras l\'appel quand on aura besoin de toi. Envole-toi vers l\'est, vers Skettis, et attends le signal |2 $n." WHERE entry = 2000005674;
UPDATE dbscript_string SET content_loc2 = "%s salue la crécerelle qui a aidé à localiser les pierres du Corbeau." WHERE entry = 2000005675;
UPDATE dbscript_string SET content_loc2 = "Alors mon sang ne vous a pas suffi, hein ? Très bien, nous allons récupérer votre essence arcanique. Et après, je ne devrai plus rien à Balthas." WHERE entry = 2000005713;
UPDATE dbscript_string SET content_loc2 = "%s pose un fétiche sur la pile rituelle." WHERE entry = 2000005714;
UPDATE dbscript_string SET content_loc2 = "C\'est une tâche ardue. Il faut d\'abord invoquer des acolytes élémentaires, puis vous devez les détruire, pour que mes serviteurs puissent commencer les préparations." WHERE entry = 2000005715;
UPDATE dbscript_string SET content_loc2 = "Bien joué ! Poursuivons." WHERE entry = 2000005716;
UPDATE dbscript_string SET content_loc2 = "Préparez-vous ! L\'acolyte d\'eau sera bientôt là." WHERE entry = 2000005717;
UPDATE dbscript_string SET content_loc2 = "Apparais, acolyte de terre !" WHERE entry = 2000005718;
UPDATE dbscript_string SET content_loc2 = "Feu, montre-toi !" WHERE entry = 2000005719;
UPDATE dbscript_string SET content_loc2 = "Invoquons maintenant l\'acolyte des Arcanes." WHERE entry = 2000005720;
UPDATE dbscript_string SET content_loc2 = "Elle vous appartient, mon Seigneur !" WHERE entry = 2000005721;
UPDATE dbscript_string SET content_loc2 = "Reculez, $n. Les esprits pourraient s\'en prendre à vous." WHERE entry = 2000005489;
UPDATE dbscript_string SET content_loc2 = "Pourquoi nous avez-vous capturés ?" WHERE entry = 2000005490;
UPDATE dbscript_string SET content_loc2 = "Qu\'espérez-vous en retirer ?" WHERE entry = 2000005491;
UPDATE dbscript_string SET content_loc2 = "%s s\'incline." WHERE entry = 2000005492;
UPDATE dbscript_string SET content_loc2 = "Nous voulons vous aider, esprit. Nous faisons appel à votre sagesse." WHERE entry = 2000005493;
UPDATE dbscript_string SET content_loc2 = "Sagesse ? C\'est votre peuple qui a éveillé l\'épouvantable colère de Cyrukh." WHERE entry = 2000005494;
UPDATE dbscript_string SET content_loc2 = "Pourquoi devrions-nous vous aider ? Le monde est en ruine. Anéanti." WHERE entry = 2000005495;
UPDATE dbscript_string SET content_loc2 = "%s hoche la tête." WHERE entry = 2000005496;
UPDATE dbscript_string SET content_loc2 = "Vous dites vrai, esprit. C\'est mon peuple qui vous a infligé cela. Permettez-nous de nous racheter." WHERE entry = 2000005497;
UPDATE dbscript_string SET content_loc2 = "Dites-nous, je vous prie : qui est Cyrukh, ou qu\'est-ce ?" WHERE entry = 2000005498;
UPDATE dbscript_string SET content_loc2 = "Idiot ! Cyrukh le seigneur du Feu ! Quand le lien fut rompu, Cyrukh s\'éveilla. Ce sont les larmes de Cyrukh qui couvrent ce monde de roche en fusion." WHERE entry = 2000005499;
UPDATE dbscript_string SET content_loc2 = "Le... Le seigneur du Feu ? Un seigneur élémentaire fut exhumé ? Mais bien sûr... Gul\'dan, tu n\'étais qu\'un satané imbécile !" WHERE entry = 2000005500;
UPDATE dbscript_string SET content_loc2 = "Il est devenu fou. Il a plongé dans la démence pendant la rupture. Il n\'y a pas de répit pour les éléments." WHERE entry = 2000005501;
UPDATE dbscript_string SET content_loc2 = "Il doit y avoir un moyen." WHERE entry = 2000005502;
UPDATE dbscript_string SET content_loc2 = "Il y en a un, oui. Détruire Cyrukh. Vaincre un seigneur élémentaire." WHERE entry = 2000005503;
UPDATE dbscript_string SET content_loc2 = "Ayez foi en mes compagnons et moi, esprit. Dites-nous comment faire apparaître le seigneur du Feu. Mes alliés le détruiront." WHERE entry = 2000005504;
UPDATE dbscript_string SET content_loc2 = "Vous devez dire les mots..." WHERE entry = 2000005505;
UPDATE dbscript_string SET content_loc2 = "Oui... Oui... les mots. Les mots de damnation. Les mots prononcés par le ténébreux. Votre maître." WHERE entry = 2000005506;
UPDATE dbscript_string SET content_loc2 = "Oui... Gul\'dan..." WHERE entry = 2000005507;
UPDATE dbscript_string SET content_loc2 = "Nous découvrirons ces mots. Nous invoquerons votre maître et nous le détruirons." WHERE entry = 2000005508;
UPDATE dbscript_string SET content_loc2 = "Des paroles. Encore, toujours des paroles. Maintenant, relâchez-nous, mortel." WHERE entry = 2000005509;
UPDATE dbscript_string SET content_loc2 = "Et maintenant, retrouvons les mots oubliés !" WHERE entry = 2000005510;
UPDATE dbscript_string SET content_loc2 = "J\'ai installé quelques consoles de contrôle plus bas. Trouvez-en une rapidement et prenez contrôle du saccageur gangrené ! Vous n\'aurez qu\'un temps limité pour y arriver." WHERE entry = 2000005459;
UPDATE dbscript_string SET content_loc2 = "À vos ordres, chef !" WHERE entry = 2000005465;
UPDATE dbscript_string SET content_loc2 = "Allie la force des géants d\'Ombrelune à la nôtre, Corok. Le maître est un chef juste. Il s\'assurera de bien prendre soin de ceux qui ont aidé à la sujétion des dissidents de ce monde." WHERE entry = 2000005466;
UPDATE dbscript_string SET content_loc2 = "Et... si on ne le fait pas ?" WHERE entry = 2000005467;
UPDATE dbscript_string SET content_loc2 = "Alors vous deviendrez les ennemis de mon seigneur, Illidan. Les ennemis d\'Illidan sont anéantis. Tes géants sont-ils capables de résister à une attaque des forces de la halte de l\'Éclipse ?" WHERE entry = 2000005468;
UPDATE dbscript_string SET content_loc2 = "Non... C\'est bien ce qu\'il me semblait..." WHERE entry = 2000005469;
UPDATE dbscript_string SET content_loc2 = "Continuez cet excellent travail. Si vous obtenez de bons résultats, le maître vous récompensera." WHERE entry = 2000005470;
UPDATE dbscript_string SET content_loc2 = "Libérez-moi immédiatement ou affrontez mon courroux !" WHERE entry = 2000005474;
UPDATE dbscript_string SET content_loc2 = "Cette cellule ne me retiendra pas longtemps. D\'une façon ou d\'une autre, j\'aurai la tête d\'Illidan." WHERE entry = 2000005475;
UPDATE dbscript_string SET content_loc2 = "Considérez ça comme un simple échauffement ! Votre vrai entraînement sera bien plus difficile !" WHERE entry = 2000005476;
UPDATE dbscript_string SET content_loc2 = "Qui peut r\'fuser une récompense comme ça, les gars ?" WHERE entry = 2000005477;
UPDATE dbscript_string SET content_loc2 = "Comment osez-vous vous amuser avec mes jouets, $n !" WHERE entry = 2000006081;
UPDATE dbscript_string SET content_loc2 = "Une voix profonde, à vous glacer les os, retentit en provenance de %s..." WHERE entry = 2000006080;
UPDATE dbscript_string SET content_loc2 = "Je vais commencer à ne plus demander gentiment, homme-poisson." WHERE entry = 2000006000;
UPDATE dbscript_string SET content_loc2 = "Où est la Formule de damnation ? Je sais qu\'une des trois parties se trouve dans ce village." WHERE entry = 2000006001;
UPDATE dbscript_string SET content_loc2 = "Qu\'est-sssce que... vous voulez... peau-lissse ?" WHERE entry = 2000006002;
UPDATE dbscript_string SET content_loc2 = "Les nagas... n\'ont pas... ssscette formule..." WHERE entry = 2000006003;
