UPDATE dbscript_string SET content_loc2 = "Sacrebleu, cette bière brune de la Griffe noire tape dur !" WHERE entry = 2000009020;
UPDATE dbscript_string SET content_loc2 = "J\’ai la tête... qui tourne. Il fait tellement... chaud... ici..." WHERE entry = 2000009021;
UPDATE dbscript_string SET content_loc2 = "Je... Je... ne me... sens... pas bien..." WHERE entry = 2000009022;
UPDATE dbscript_string SET content_loc2 = "Mon esprit... ma chair... Je suis... en train de pourrir...!" WHERE entry = 2000009023;
UPDATE dbscript_string SET content_loc2 = "%s essaie d\’ouvrir le pied bot de Maury..." WHERE entry = 2000009024;
UPDATE dbscript_string SET content_loc2 = "Ah ah ! Ça s\'ouvre ! Je m\'attendais à ce que ça soit plus difficile. Merci pour votre aide, $n." WHERE entry = 2000009025;
UPDATE dbscript_string SET content_loc2 = "Merci, $n." WHERE entry = 2000000130;
UPDATE dbscript_string SET content_loc2 = "Je suis sûr de trouver un usage pour cette mixture..." WHERE entry = 2000000131;
UPDATE dbscript_string SET content_loc2 = "Merci encore pour votre aide. Puisse le vent se trouver toujours dans votre dos durant vos périples." WHERE entry = 2000009026;
UPDATE dbscript_string SET content_loc2 = "Plus facile que je ne le croyais... le cachet de Thoradin est de nouveau complet." WHERE entry = 2000009027;
UPDATE dbscript_string SET content_loc2 = "Attendez, $n !!! J\’ai l\’impression que nous allons avoir besoin d\’aide ici..." WHERE entry = 2000005088;
UPDATE dbscript_string SET content_loc2 = "%s sent bouillonner son sang et la ruse du raptor entre dans son esprit." WHERE entry = 2000000115;
UPDATE dbscript_string SET content_loc2 = "Quel est ce pouvoir qui coule à travers moi ? S\’agit-il du pouvoir de chaman dont Tor\’gan a parlé...?" WHERE entry = 2000000116;
UPDATE dbscript_string SET content_loc2 = "Impressionnant ! Je me sens... revivre. Mes forces reviennent !" WHERE entry = 2000000117;
UPDATE dbscript_string SET content_loc2 = "%s commence un rite de création..." WHERE entry = 2000000170;
UPDATE dbscript_string SET content_loc2 = "J\’ai créé le Totem de purification. C\’est à toi, $n, de purifier le puits." WHERE entry = 2000000171;
