UPDATE dbscript_string SET content_loc2 = "Le totem est prêt. Le puits peut maintenant être purifié." WHERE entry = 2000000175;
UPDATE dbscript_string SET content_loc2 = "%s se met à psalmodier en mélangeant les pierres du puits et les pommes de pin avant d\'allumer le Feu du clan." WHERE entry = 2000009028;
UPDATE dbscript_string SET content_loc2 = "L\’Eau des Prophètes est prête à être bue, $n." WHERE entry = 2000009029;
UPDATE dbscript_string SET content_loc2 = "%s étudie le parchemin provenant du tome du Crépuscule..." WHERE entry = 2000009030;
UPDATE dbscript_string SET content_loc2 = "%s est frappé par un enchantement !" WHERE entry = 2000009031;
UPDATE dbscript_string SET content_loc2 = "Hum..." WHERE entry = 2000009032;
UPDATE dbscript_string SET content_loc2 = "Quel est ce bruit ? On aurait dit un mugissement." WHERE entry = 2000005384;
UPDATE dbscript_string SET content_loc2 = "%s rugit devant Terenthis pour attirer son attention." WHERE entry = 2000005385;
UPDATE dbscript_string SET content_loc2 = "Griffe-sinistre ?! Doucement mon ami... Où est ton maître Volcor ?" WHERE entry = 2000005386;
UPDATE dbscript_string SET content_loc2 = "%s se met à geindre et à mugir face à Terenthis tout en foulant lourdement le sol de ses pattes." WHERE entry = 2000005387;
UPDATE dbscript_string SET content_loc2 = "Doucement, doucement mon ami. Un moment..." WHERE entry = 2000005388;
UPDATE dbscript_string SET content_loc2 = "%s commence à lancer un sort à Griffe-sinistre." WHERE entry = 2000005389;
UPDATE dbscript_string SET content_loc2 = "Là... Ça devrait vous faire du bien. Maintenant, racontez-moi ce qui s\’est passé, Griffe-sinistre." WHERE entry = 2000005390;
UPDATE dbscript_string SET content_loc2 = "%s rugit de nouveau face à Terenthis mais cette fois le druide semble comprendre l\’ours." WHERE entry = 2000005391;
UPDATE dbscript_string SET content_loc2 = "Je comprends, mon ami. Je trouverai quelqu\’un pour venir en aide à votre maître. Retournez auprès de lui maintenant, ou tout du moins, restez à proximité." WHERE entry = 2000005392;
UPDATE dbscript_string SET content_loc2 = "%s rugit en guise d\’acquiescement avec Terenthis." WHERE entry = 2000005393;
UPDATE dbscript_string SET content_loc2 = "Si vous avez le temps, vous pourriez donner un coup de main à Griffe-sinistre et son maître Volcor. Si cela vous intéresse, faites-le-moi savoir..." WHERE entry = 2000005394;
UPDATE dbscript_string SET content_loc2 = "Les esprits des Serres-Rocheuses sont encore furieux, $n." WHERE entry = 2000000105;
UPDATE dbscript_string SET content_loc2 = "J\’ai bien peur que nous ne puissions jamais les apaiser..." WHERE entry = 2000000106;
