UPDATE dbscript_string SET content_loc2 = "Atchoum !" WHERE entry = 2000000128;
UPDATE dbscript_string SET content_loc2 = "Wouah ! $n, cette poussière de rêve, c\’est drôlement puissant !" WHERE entry = 2000000129;
UPDATE dbscript_string SET content_loc2 = "%s se met à danser." WHERE entry = 2000005083;
UPDATE dbscript_string SET content_loc2 = "Ha ha ha ! $n, vous êtes $gun sacré:une sacrée; partenaire !" WHERE entry = 2000005084;
