UPDATE dbscript_string SET content_loc2 = "La progéniture d\’Onyxia !" WHERE entry = 2000000125;
UPDATE dbscript_string SET content_loc2 = "OK, les gars. Installez ces os." WHERE entry = 2000000114;
UPDATE dbscript_string SET content_loc2 = "Préparer le sérum ne prendra que quelques minutes, $n." WHERE entry = 2000009033;
UPDATE dbscript_string SET content_loc2 = "Presque prêt..." WHERE entry = 2000009034;
UPDATE dbscript_string SET content_loc2 = "Le sérum est enfin terminé, $n. Approchez-vous à présent. Nous devons parler." WHERE entry = 2000009035;
UPDATE dbscript_string SET content_loc2 = "%s prend une grosse gorgée de bière." WHERE entry = 2000000119;
UPDATE dbscript_string SET content_loc2 = "Ça rafraîchit. Je dois maintenant vous communiquer des informations..." WHERE entry = 2000000120;
UPDATE dbscript_string SET content_loc2 = "Je crois que les Réprouvés sont en train de tromper les alliés de la Horde... attendez... J\'ai... la tête qui tourne..." WHERE entry = 2000000121;
UPDATE dbscript_string SET content_loc2 = "%s se tord de douleur." WHERE entry = 2000000122;
UPDATE dbscript_string SET content_loc2 = "AAAAAAAAAAAAAAAARGH !" WHERE entry = 2000000123;
