UPDATE dbscript_string SET content_loc2 = "Personnellement, il ne me dérangeait pas. C\’est le prince qui a mal pris la présence d\’un haut elfe dans son domaine. Hélas, je ne suis pas de ceux qui remettent en question la volonté du prince. Pour sa défense, il n\’a pas incinéré maître Winthalus sur-le-champ." WHERE entry = 2000000456;
